INSERT INTO `pif_management` (`id`, `name`, `manage_type`, `position`, `biography`, `avatar`) VALUES
(1, 'ÔNG LÃ GIANG TRUNG', 1, 'Tổng giám đốc', '<ul>\r\n	<li>&Ocirc;ng L&atilde; Giang Trung c&oacute; tr&ecirc;n 15 năm kinh nghiệm trong lĩnh vực T&agrave;i ch&iacute;nh, Chứng kho&aacute;n v&agrave; Đầu tư tại Việt Nam. Trước khi th&agrave;nh lập Passion Investment v&agrave;o năm 2015, &ocirc;ng đ&atilde; c&oacute; nhiều năm l&agrave;m việc tại c&aacute;c C&ocirc;ng ty Quản l&yacute; Quỹ, C&ocirc;ng ty Chứng kho&aacute;n. Dưới sự điều h&agrave;nh của &ocirc;ng, Passion Investment đ&atilde; trở th&agrave;nh một trong những c&ocirc;ng ty đầu tư chứng kho&aacute;n c&oacute; hiệu quả tốt nhất tại thị trường Việt Nam. &Ocirc;ng Trung hiện cũng đang l&agrave; Th&agrave;nh vi&ecirc;n Hội đồng Quản trị ki&ecirc;m Tổng gi&aacute;m đốc C&ocirc;ng ty Cổ phần Hestia &ndash; c&ocirc;ng ty v&ecirc;̀ đ&acirc;̀u tư tài chính đang được giao dịch tr&ecirc;n sàn Upcom với mã chứng khoán là HSA.</li>\r\n	<li>&Ocirc;ng Trung tốt nghiệp Thạc sỹ chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh tại Đại học Monash &ndash; &Uacute;c, Cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh Ng&acirc;n h&agrave;ng tại Đại học Kinh tế Quốc d&acirc;n H&agrave; Nội v&agrave; trở th&agrave;nh CFA CharterHolder từ năm 2009.</li>\r\n</ul>', 'la-giang-trung.png'),
(2, 'ÔNG THÁI DOÃN KIÊN', 1, 'Chuyên viên đầu tư', '<ul>\r\n	<li>&Ocirc;ng Th&aacute;i Do&atilde;n Ki&ecirc;n đ&atilde; c&oacute; hơn 7 năm kinh nghiệm trong lĩnh vực đầu tư. Trước khi gia nhập Passion Investment v&agrave;o năm 2018, &ocirc;ng Ki&ecirc;n đ&atilde; c&oacute; hơn 5 năm kinh nghiệm trong lĩnh vực t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng v&agrave; l&agrave; chuy&ecirc;n gia thẩm định t&iacute;n dụng tại c&aacute;c ng&acirc;n h&agrave;ng lớn tại Việt Nam.</li>\r\n	<li>Hiện nay &ocirc;ng Ki&ecirc;n chịu tr&aacute;ch nhiệm nghi&ecirc;n cứu, ph&acirc;n t&iacute;ch, đề xuất c&aacute;c cơ hội đầu tư tiềm năng cho danh mục của c&ocirc;ng ty v&agrave; được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n đầu tư hiệu quả, nhạy b&eacute;n v&agrave; biết tận dụng c&aacute;c xu thế của thị trường. &Ocirc;ng Ki&ecirc;n tốt nghiệp cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh tại trường Học viện Ng&acirc;n h&agrave;ng.</li>\r\n</ul>', 'thai-doan-kien.png'),
(3, 'ÔNG BÙI TRƯỜNG GIANG', 1, 'Chuyên viên đầu tư', '<ul>\r\n	<li>&Ocirc;ng B&ugrave;i Trường Giang c&oacute; nhiều năm kinh nghiệm trong lĩnh vực đầu tư tr&ecirc;n thị trường chứng kho&aacute;n Việt Nam. &Ocirc;ng tốt nghiệp cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh Kinh tế tại Đại học Anh Quốc Việt Nam đồng thời được nhận bằng k&eacute;p của trường Đại học Anh Quốc Việt Nam v&agrave; Trường Đại học Staffordshire, UK.</li>\r\n	<li>&Ocirc;ng Giang được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n ham học hỏi, c&oacute; kế hoạch đầu tư hiệu quả, năng động v&agrave; tinh thần l&agrave;m việc nhiệt huyết. &Ocirc;ng Giang l&agrave; một trong những người truyền cảm hứng kh&ocirc;ng thể thiếu của Passion Investment. Hiện &ocirc;ng Giang phụ tr&aacute;ch hoạch định, nghi&ecirc;n cứu v&agrave; đề xuất c&aacute;c cơ hội đầu tư tiềm năng cho c&ocirc;ng ty.</li>\r\n</ul>', 'bui-truong-giang.png'),
(4, 'ÔNG TRẦN BÌNH DƯƠNG', 1, 'Chuyên viên đầu tư', '<ul>\r\n	<li>L&agrave; một người tham gia thị trường chứng kho&aacute;n từ rất sớm khi c&ograve;n l&agrave; sinh vi&ecirc;n, &ocirc;ng Trần B&igrave;nh Dương đ&atilde; bồi đắp li&ecirc;n tục năng lực trong việc ph&acirc;n t&iacute;ch ng&agrave;nh v&agrave; doanh nghiệp của từng giai đoạn thị trường để t&igrave;m ra những cơ hội đầu tư hiệu quả. Gia nhập Passion Investment từ năm 2019, &ocirc;ng hiện l&agrave; Chuy&ecirc;n vi&ecirc;n đầu tư chịu tr&aacute;ch nhiệm hoạch định, ph&acirc;n t&iacute;ch v&agrave; đề xuất c&aacute;c cơ hội đầu tư tiềm năng cho danh mục đầu tư của c&ocirc;ng ty.</li>\r\n	<li>&Ocirc;ng Dương được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n năng động, c&oacute; kỷ luật v&agrave; tinh thần l&agrave;m việc nhiệt huyết g&oacute;p phần kh&ocirc;ng nhỏ tạo m&ocirc;i trường l&agrave;m việc hiệu quả v&agrave; th&acirc;n thiện. &Ocirc;ng tốt nghiệp cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh Quốc tế tại Đại học Ngoại thương v&agrave; đ&atilde; vượt qua Level I chương tr&igrave;nh CFA (12/2019), top 10%.</li>\r\n</ul>', 'tran-binh-duong.png'),
(5, '\r\nÔNG VŨ QUANG MINH', 1, 'Chuyên viên đầu tư', '<ul>\r\n	<li>Sớm tiếp x&uacute;c với lĩnh vực T&agrave;i ch&iacute;nh, &ocirc;ng Vũ Quang Minh đ&atilde; c&oacute; kinh nghiệm đầu tư tr&ecirc;n thị trường chứng kho&aacute;n Mỹ v&agrave; Việt Nam kể từ khi l&agrave; sinh vi&ecirc;n. &Ocirc;ng gia nhập Passion Investment từ năm 2020, v&agrave; hiện l&agrave; Chuy&ecirc;n vi&ecirc;n Đầu tư chịu tr&aacute;ch nhiệm nghi&ecirc;n cứu, ph&acirc;n t&iacute;ch v&agrave; đề xuất c&aacute;c cơ hội đầu tư tiềm năng cho danh mục đầu tư của c&ocirc;ng ty.</li>\r\n	<li>&Ocirc;ng Minh được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n năng động, ham học hỏi v&agrave; c&oacute; tinh thần kỷ luật cao trong c&ocirc;ng việc. &Ocirc;ng tốt nghiệp bằng xuất sắc cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh tại Đại học Auburn University (Mỹ).</li>\r\n</ul>', 'vu-quang-minh.png'),
(6, 'NGUYỄN TRUNG HIẾU', 1, 'Chuyên viên đầu tư', '<ul>\r\n	<li>&Ocirc;ng Hiếu tốt nghiệp bằng giỏi cử nh&acirc;n chuy&ecirc;n ng&agrave;nh Kinh tế T&agrave;i ch&iacute;nh tại Đại học RMIT Việt Nam.</li>\r\n	<li>Trước khi gia nhập Passion Investment, &ocirc;ng Hiếu từng c&oacute; kinh nghiệm l&agrave;m việc trong lĩnh vực kiểm to&aacute;n tại C&ocirc;ng ty TNHH KPMG Việt Nam.</li>\r\n	<li>&Ocirc;ng Hiếu hiện đang l&agrave;m việc tại đội đầu tư của C&ocirc;ng ty Cổ phần Passion Investment.</li>\r\n</ul>', 'nguyen-trung-hieu.png'),
(7, 'BÀ CHU MINH NGỌC', 2, 'Phó Tổng Giám đốc', '<ul>\r\n	<li>B&agrave; Ngọc đ&atilde; c&oacute; hơn 13 năm kinh nghiệm trong lĩnh vực t&aacute;i cấu tr&uacute;c, tư vấn t&agrave;i ch&iacute;nh doanh nghiệp v&agrave; tư vấn mua b&aacute;n s&aacute;p nhập doanh nghiệp. Trước khi th&agrave;nh lập Passion Investment, B&agrave; l&agrave; chuy&ecirc;n gia tư vấn t&agrave;i ch&iacute;nh doanh nghiệp, phụ tr&aacute;ch nhiều dự &aacute;n quan trọng về x&aacute;c định gi&aacute; trị doanh nghiệp, x&acirc;y dựng phương &aacute;n cổ phần h&oacute;a, b&aacute;n đấu gi&aacute; cổ phần tại C&ocirc;ng ty Chứng kho&aacute;n Bản Việt v&agrave; tư vấn M&amp;A tại C&ocirc;ng ty Chứng kho&aacute;n Dầu kh&iacute;.</li>\r\n	<li>B&agrave; tốt nghiệp cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh Ng&acirc;n h&agrave;ng tại trường Đại học Kinh tế Quốc d&acirc;n H&agrave; Nội v&agrave; thạc sỹ chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh tại Đại học Deakin, &Uacute;c.</li>\r\n	<li>B&agrave; hiện đang nắm giữ vị trí Gi&aacute;m đốc Vận h&agrave;nh tại C&ocirc;ng ty C&ocirc;̉ ph&acirc;̀n Hestia.</li>\r\n</ul>', 'chu-minh-ngoc.png'),
(8, 'BÀ NGUYỄN THỊ HỒNG ANH', 2, 'Hành chính - Kế toán', '<ul>\r\n	<li>B&agrave; Nguyễn Thị Hồng Anh đ&atilde; c&oacute; hơn 7 năm kinh nghiệm trong lĩnh vực h&agrave;nh ch&iacute;nh v&agrave; kế to&aacute;n. Gia nhập Passion Investment từ những ng&agrave;y đầu th&agrave;nh lập, b&agrave; Hồng Anh hiện l&agrave; quản l&yacute; mảng h&agrave;nh ch&iacute;nh kế to&aacute;n của c&ocirc;ng ty chịu tr&aacute;ch nhiệm xử l&yacute; c&aacute;c dữ liệu kế to&aacute;n, ph&acirc;n t&iacute;ch t&igrave;nh h&igrave;nh t&agrave;i ch&iacute;nh, chi ph&iacute;, doanh thu để tham mưu cho ban l&atilde;nh đạo c&ocirc;ng ty.</li>\r\n	<li>B&agrave; Hồng Anh được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n chăm chỉ, cẩn thận, ham học hỏi. B&agrave; đảm nhận việc k&iacute;ch th&iacute;ch v&agrave; điều tiết c&aacute;c hoạt động của Passion Investment, duy tr&igrave; v&agrave; ph&aacute;t triển c&aacute;c mối li&ecirc;n kết trong doanh nghiệp đồng thời g&oacute;p phần tạo m&ocirc;i trường l&agrave;m việc hiệu quả v&agrave; th&acirc;n thiện.</li>\r\n</ul>', 'nguyen-thi-hong-anh.png'),
(9, 'BÀ VŨ KIỀU NGA', 2, 'Hành chính - Kế toán', '<ul>\r\n	<li>B&agrave; Vũ Kiều Nga đ&atilde; c&oacute; hơn 6 năm kinh nghiệm trong lĩnh vực h&agrave;nh ch&iacute;nh, nh&acirc;n sự v&agrave; chăm s&oacute;c Kh&aacute;ch h&agrave;ng. Gia nhập Passion Investment từ năm 2019, b&agrave; Nga hiện đang phụ tr&aacute;ch mảng dịch vụ kh&aacute;ch h&agrave;ng chịu tr&aacute;ch nhiệm tư vấn dịch vụ, tiếp nhận v&agrave; xử l&yacute; y&ecirc;u cầu/thắc mắc của kh&aacute;ch h&agrave;ng li&ecirc;n quan đến sản phẩm, chăm s&oacute;c mối quan hệ với kh&aacute;ch h&agrave;ng v&agrave; đưa ra đề xuất cải thiện, n&acirc;ng cao chất lượng c&ocirc;ng việc.</li>\r\n	<li>B&agrave; Nga được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n ham học hỏi, năng động v&agrave; tinh thần l&agrave;m việc nhiệt huyết. Với kĩ năng lắng nghe v&agrave; thuyết phục kh&aacute;ch h&agrave;ng, b&agrave; đ&atilde; gi&uacute;p duy tr&igrave; v&agrave; ph&aacute;t triển c&aacute;c mối quan hệ với kh&aacute;ch h&agrave;ng đồng thời g&oacute;p phần th&uacute;c đẩy sự ph&aacute;t triển của Passion Investment.</li>\r\n</ul>', 'vu-kieu-nga.png'),
(10, 'LÊ THỊ SAO', 2, 'Hành chính - Kế toán', '<ul>\r\n	<li>B&agrave; Sao tốt nghiệp cử nh&acirc;n chuy&ecirc;n ng&agrave;nh kế to&aacute;n, trường Đại học Thương Mại</li>\r\n	<li>B&agrave; Sao đ&atilde; c&oacute; hơn 10 năm kinh nghiệm trong lĩnh vực kế to&aacute;n.</li>\r\n	<li>Trước khi gia nhập Passion Investment, b&agrave; Sao đ&atilde; c&oacute; kinh nghiệm l&agrave;m việc trong lĩnh vực kế to&aacute;n tại Tập đo&agrave;n ONSEN FUJI.</li>\r\n</ul>', 'le-thi-sao.png'),
(11, 'BÀ NGUYỄN THỊ CẨM DƯƠNG', 2, 'Hành chính - Nhân sự', '<ul>\r\n	<li>B&agrave; Nguyễn Thị Cẩm Dương đ&atilde; c&oacute; hơn 7 năm kinh nghiệm trong lĩnh vực h&agrave;nh ch&iacute;nh v&agrave; nh&acirc;n sự. Gia nhập Passion Investment từ năm 2019, b&agrave; Dương hiện đang phụ tr&aacute;ch mảng h&agrave;nh ch&iacute;nh nh&acirc;n sự của c&ocirc;ng ty chịu tr&aacute;ch nhiệm c&aacute;c vấn đề h&agrave;nh ch&iacute;nh, quản l&yacute; ch&iacute;nh s&aacute;ch v&agrave; đề ra ch&iacute;nh s&aacute;ch li&ecirc;n quan đến t&agrave;i nguy&ecirc;n nh&acirc;n sự.</li>\r\n	<li>B&agrave; Dương được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n ham học hỏi, năng động v&agrave; tinh thần l&agrave;m việc nhiệt huyết.</li>\r\n</ul>', 'nguyen-thi-cam-duong.png'),
(12, 'BÀ NGUYỄN HỒNG ANH', 2, 'Dịch vụ Khách hàng', '<ul>\r\n	<li>B&agrave; Nguyễn Hồng Anh đ&atilde; c&oacute; hơn 7 năm kinh nghiệm trong lĩnh vực tư vấn dịch vụ v&agrave; chăm s&oacute;c Kh&aacute;ch h&agrave;ng. Gia nhập Passion Investment năm 2017, b&agrave; Hồng Anh hiện l&agrave; Chuy&ecirc;n vi&ecirc;n chăm s&oacute;c kh&aacute;ch h&agrave;ng chịu tr&aacute;ch nhiệm tiếp nhận v&agrave; xử l&yacute; y&ecirc;u cầu, duy tr&igrave; v&agrave; chăm s&oacute;c mối quan hệ với kh&aacute;ch h&agrave;ng, thiết lập, đo lường mức độ h&agrave;i l&ograve;ng của kh&aacute;ch về sản phẩm của Passion Investment.<br />\r\n	B&agrave; Hồng Anh được ghi nhận l&agrave; một chuy&ecirc;n vi&ecirc;n năng động, chăm chỉ v&agrave; đầy nhiệt huyết. Với nền tảng kiến thức vững chắc v&agrave; am hiểu t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng, b&agrave; đ&atilde; gi&uacute;p duy tr&igrave; v&agrave; ph&aacute;t triển c&aacute;c mối quan hệ với kh&aacute;ch h&agrave;ng đồng thời g&oacute;p phần th&uacute;c đẩy sự ph&aacute;t triển của Passion Investment.</li>\r\n</ul>', 'nguyen-hong-anh.png'),
(13, 'BÀ PHẠM THỊ QUỲNH TRANG', 2, 'Dịch vụ Khách hàng', '<ul>\r\n	<li>B&agrave; Phạm Thị Quỳnh Trang l&agrave; cử nh&acirc;n chuy&ecirc;n ng&agrave;nh T&agrave;i ch&iacute;nh &ndash; Ng&acirc;n h&agrave;ng, đ&atilde; c&oacute; hơn 6 năm kinh nghiệm trong lĩnh vực tư vấn t&agrave;i ch&iacute;nh v&agrave; chăm s&oacute;c Kh&aacute;ch h&agrave;ng tại c&ocirc;ng ty Chứng Kho&aacute;n. Gia nhập Passion Investment năm 2017, b&agrave; Quỳnh Trang hiện đang phụ tr&aacute;ch mảng chăm s&oacute;c kh&aacute;ch h&agrave;ng chịu tr&aacute;ch nhiệm tư vấn dịch vụ, tiếp nhận v&agrave; xử l&yacute; c&aacute;c y&ecirc;u cầu, chăm s&oacute;c mối quan hệ với kh&aacute;ch h&agrave;ng.</li>\r\n	<li>Với nền tảng kiến thức vững chắc, kĩ năng lắng nghe v&agrave; thuyết phục kh&aacute;ch h&agrave;ng, b&agrave; Trang đ&atilde; gi&uacute;p duy tr&igrave; v&agrave; ph&aacute;t triển c&aacute;c mối quan hệ với kh&aacute;ch h&agrave;ng đồng thời g&oacute;p phần th&uacute;c đẩy sự ph&aacute;t triển của Passion Investment.</li>\r\n</ul>', 'pham-thi-quynh-trang.png'),
(14, 'BÀ TRẦN HỒNG HẠNH', 2, 'Dịch vụ Khách hàng', '<ul>\r\n	<li>B&agrave; Trần Hồng Hạnh tốt nghiệp chuy&ecirc;n ng&agrave;nh Ng&ocirc;n ngữ Anh, được trang bị kiến thức về kỹ năng giao tiếp, khả năng thuyết tr&igrave;nh, ph&acirc;n t&iacute;ch v&agrave; giải quyết vấn đề nhanh c&ugrave;ng với kinh nghiệm l&agrave;m việc gần 5 năm trong ng&agrave;nh dịch vụ ở vị tr&iacute; chăm s&oacute;c kh&aacute;ch h&agrave;ng.</li>\r\n	<li>B&agrave; Hạnh được tin tưởng sẽ trở th&agrave;nh một nh&acirc;n vi&ecirc;n chăm s&oacute;c kh&aacute;ch h&agrave;ng chuy&ecirc;n nghiệp, đồng thời x&acirc;y dựng được mối qu&acirc;n hệ tốt với c&aacute;c kh&aacute;ch h&agrave;ng tại Passion Investment.</li>\r\n</ul>', 'tran-hong-hanh.png');

