--
-- Table structure for table `pif_recruitment`
--

DROP TABLE IF EXISTS `pif_recruitment`;
CREATE TABLE IF NOT EXISTS `pif_recruitment` (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `slug` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `introtext` mediumtext CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `fulltext` mediumtext CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `state` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  `catid` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  `created_by` int(11) NOT NULL DEFAULT '0',
  `modified` datetime NOT NULL,
  `modified_by` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `checked_out` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `checked_out_time` datetime NOT NULL,
  `publish_up` datetime NOT NULL,
  `publish_down` datetime NOT NULL,
  `images` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `attribs` varchar(5120) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `ordering` int(11) NOT NULL DEFAULT '0',
  `metakey` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `metadesc` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `hits` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `featured` tinyint(3) UNSIGNED NOT NULL DEFAULT '0' COMMENT 'Set if article is featured.',
  `language` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT 'The language code for the article.',
  `note` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT '',
  `remember_token` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `pif_recruitment`
--

INSERT INTO `pif_recruitment` (`id`, `title`, `slug`, `introtext`, `fulltext`, `state`, `catid`, `created_by`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `attribs`, `ordering`, `metakey`, `metadesc`, `hits`, `featured`, `language`, `note`, `remember_token`, `created_at`, `updated_at`) VALUES
(1, 'Tuyển dụng thực tập viên phân tích cổ phiếu', 'tuyen-dung-thuc-tap-vien-phan-tich-co-phieu', 'Bạn có muốn được hướng dẫn đào tạo kỹ năng nghiệp vụ cũng như hoàn thiện kiến thức tài chính? Bạn có muốn trải nghiệm môi trường làm việc chuyên nghiệp của một quỹ đầu tư và có cơ hội nghề nghiệp trong tương lai?', '<p><strong>Bạn đang l&agrave; sinh vi&ecirc;n nhưng c&oacute; niềm đam m&ecirc; với lĩnh vực đầu tư?</strong></p>\r\n\r\n<p>Passion Investment ch&iacute;nh l&agrave; lựa chọn ho&agrave;n hảo cho bạn với chương tr&igrave;nh Internship đầy th&aacute;ch thức khi bạn được l&agrave;m việc, va chạm thực tế khốc liệt trong lĩnh vực đầu tư v&agrave; được hướng dẫn bởi c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu trong lĩnh vực n&agrave;y.</p>\r\n\r\n<p><strong>M&ocirc; tả c&ocirc;ng việc</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Trực ti&ecirc;́p thẩm định, đ&aacute;nh gi&aacute;, định gi&aacute; m&ocirc;̣t s&ocirc;́ cơ hội đầu tư được giao</li>\r\n	<li>Theo d&otilde;i thị trường, cập nhật th&ocirc;ng tin của c&aacute;c doanh nghiệp đ&atilde; đầu tư, để đề xuất việc nắm giữ, mua, b&aacute;n v&agrave; mức giá hỗ trợ vi&ecirc;̣c ra quyết định đầu tư, thanh ho&aacute;n c&oacute; hiệu quả.</li>\r\n	<li>Thực hiện vi&ecirc;̣c tìm ki&ecirc;́m v&agrave; đ&aacute;nh gi&aacute; các cơ h&ocirc;̣i đ&acirc;̀u tư mới.</li>\r\n	<li>C&ocirc;ng việc nghi&ecirc;n cứu kh&aacute;c: Nghi&ecirc;n cứu các c&ocirc;ng cụ h&ocirc;̃ trợ, nghi&ecirc;n cứu c&aacute;c sản phẩm mới.</li>\r\n</ul>\r\n\r\n<p><strong>Quyền lợi</strong></p>\r\n\r\n<ul>\r\n	<li>Được hướng dẫn đ&agrave;o tạo kỹ năng nghiệp vụ cũng như ho&agrave;n thiện kiến thức t&agrave;i ch&iacute;nh.</li>\r\n	<li>Trực tiếp tham gia v&agrave;o qu&aacute; tr&igrave;nh t&igrave;m kiếm cũng như nghi&ecirc;n cứu đầu tư cổ phiếu</li>\r\n	<li>Ho&agrave;n thiện mind-set về đầu tư v&agrave; được hướng dẫn c&aacute;c bước cơ bản để ph&acirc;n t&iacute;ch một cơ hội đầu tư</li>\r\n	<li>Cơ hội được học c&aacute;ch x&acirc;y dựng b&aacute;o c&aacute;o ph&acirc;n t&iacute;ch ng&agrave;nh, doanh nghiệp để đầu tư.</li>\r\n	<li>Cơ hội trải nghiệm m&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp của một quỹ đầu tư</li>\r\n	<li>Cơ hội nghề nghiệp trong tương lai</li>\r\n</ul>\r\n\r\n<p><strong>Y&ecirc;u cầu</strong></p>\r\n\r\n<ul>\r\n	<li>C&oacute; kiến thức cơ bản về t&agrave;i ch&iacute;nh</li>\r\n	<li>C&oacute; đam m&ecirc; về thị trường chứng kho&aacute;n, đầu tư</li>\r\n	<li>Đ&atilde; vượt qua hoặc đang học Level 2 CFA l&agrave; một lợi thế</li>\r\n</ul>\r\n\r\n<p><strong>Li&ecirc;n hệ</strong></p>\r\n\r\n<ul>\r\n	<li>Hồ sơ dự tuyển bao gồm: sơ yếu l&yacute; lịch, thư xin việc&nbsp;(thể hiện r&otilde; đam m&ecirc; của bạn với lĩnh vực đầu tư, đ&acirc;y ch&iacute;nh l&agrave; yếu tố để PI quyết định c&oacute; nhận một ứng vi&ecirc;n v&agrave;o chương tr&igrave;nh thực tập hay kh&ocirc;ng).</li>\r\n	<li>Hồ sơ xin gửi qua email: nhansu@pif.vn&nbsp;<em>&nbsp;(ghi r&otilde; t&ecirc;n v&agrave; vị tr&iacute; ứng tuyển tr&ecirc;n ti&ecirc;u đề. V&iacute; dụ: Nguyen Van A &ndash; Thuc tap vien phan tich).</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Mọi thắc mắc xin li&ecirc;n hệ qua email hoặc Văn ph&ograve;ng c&ocirc;ng ty:&nbsp;Tầng&nbsp;26, T&ograve;a nh&agrave; Mipec, Số&nbsp;229, Quận Đống Đa, H&agrave; Nội</li>\r\n</ul>', '0', '0', 0, '2016-08-18 17:37:59', 0, 0, '2016-08-18 17:37:59', '2016-08-18 17:37:59', '2016-08-18 17:37:59', 'recruitments/l4d0QZWLg5IoJszFoZq1iu0ZszVl3QuQZoHfA6g0.jpg', 'trade', 0, 'tuyển dụng, cổ phiếu', 'phân tích', 0, 0, 'vietnam', 'hết hạn', NULL, '2016-08-18 10:37:59', '2023-02-24 00:48:11'),
(2, 'Tuyển dụng nhân viên Lễ tân', 'tuyen-dung-nhan-vien-le-tan', 'Quyền lợi nhân viên Lễ tân:\r\n– Lương cố định 4 triệu 2, đãi ngộ hấp dẫn;\r\n– Được đóng BHXH-YT-TN theo quy định.\r\n– Làm việc từ thứ 2 đến t6 (T7-CN nghỉ)', '<ol>\r\n	<li><strong>1.M&ocirc; tả C&ocirc;ng việc:</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Nhận cuộc gọi đi đến C&ocirc;ng ty v&agrave; chuyển tới c&aacute;c ph&ograve;ng Ban Li&ecirc;n Quan.</li>\r\n	<li>Đ&oacute;n kh&aacute;ch, hướng dẫn kh&aacute;ch đến đến l&agrave;m việc tại C&ocirc;ng ty.</li>\r\n	<li>Thực hiện việc li&ecirc;n lạc đặt v&eacute; may bay, cho CBNV đi c&ocirc;ng t&aacute;c khi c&oacute; y&ecirc;u cầu.</li>\r\n	<li>Nhận, kiểm so&aacute;t v&agrave; lưu trữ c&aacute;c hồ sơ, c&aacute;c văn bản nội bộ, c&ocirc;ng văn đến (thư từ, c&ocirc;ng văn, bưu phẩm&hellip;) đ&uacute;ng quy định.</li>\r\n	<li>Kiểm so&aacute;t v&agrave; chuyển c&ocirc;ng văn, thư từ đi đến đ&uacute;ng địa chỉ v&agrave; kịp thời.</li>\r\n	<li>Quản l&yacute; con dấu v&agrave; thực hiện c&ocirc;ng t&aacute;c đ&oacute;ng dấu văn bản đ&uacute;ng quy định.</li>\r\n	<li>Soạn thảo c&aacute;c văn bản h&agrave;nh ch&iacute;nh. Chuẩn bị t&agrave;i liệu c&aacute;c cuộc họp, hội nghị.</li>\r\n	<li>C&aacute;c c&ocirc;ng việc kh&aacute;c theo y&ecirc;u cầu của Quản l&yacute;.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><strong>Quyền lợi:</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Lương 4 triệu 2.&nbsp;Hưởng đầy đủ c&aacute;c chế độ BHXH, BHYT, BHTN.</li>\r\n	<li>Lương thưởng cuối năm theo hiệu quả l&agrave;m việc v&agrave; c&aacute;c dịp lễ Tết (30/4, 2/9&hellip;)</li>\r\n	<li>M&ocirc;i trường l&agrave;m việc trẻ, năng động, được t&ocirc;n trọng v&agrave; đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n m&ocirc;n.</li>\r\n	<li>Du lịch 2 lần/năm (1 lần trong nước, 1 lần nước ngo&agrave;i);</li>\r\n	<li>L&agrave;m việc trong giờ h&agrave;nh ch&iacute;nh từ thứ 2 &ndash; 6 (nghỉ thứ 7, chủ nhật).</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><strong>Y&ecirc;u cầu:</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Giới t&iacute;nh: Nữ (ưu ti&ecirc;n ứng cử vi&ecirc;n sinh năm 1994 &ndash; 1998)</li>\r\n	<li>Ngoại h&igrave;nh ưa nh&igrave;n.</li>\r\n	<li>Chăm chỉ, thật th&agrave;, nhanh nhẹn</li>\r\n	<li>C&oacute; nhu cầu l&agrave;m việc gắn b&oacute;, l&acirc;u d&agrave;i.</li>\r\n	<li>Kh&ocirc;ng y&ecirc;u cầu bằng cấp.</li>\r\n	<li>Ưu ti&ecirc;n c&aacute;c bạn c&oacute; kinh nghiệm l&agrave;m lễ t&acirc;n.</li>\r\n</ul>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 26, T&ograve;a nh&agrave; Mipec, Số&nbsp;229&nbsp;T&acirc;y Sơn, Quận Đống Đa, H&agrave; Nội.</p>\r\n\r\n<p><strong>Ứng tuyển ngay bằng c&aacute;ch gửi CV về:&nbsp;nhansu</strong><strong>@pif.vn</strong></p>\r\n\r\n<p><strong>Để biết th&ecirc;m chi tiết vui l&ograve;ng li&ecirc;n hệ:&nbsp;</strong>(024) 32 646 480</p>\r\n\r\n<p><strong>Hoặc truy cập website:</strong>&nbsp;<a href=\"https://old.pif.vn/\"><strong>https://old.pif.vn/</strong></a></p>\r\n\r\n<h3>Giới thiệu về c&ocirc;ng ty:</h3>\r\n\r\n<p>C&ocirc;ng ty Cổ phần Passion Investment (PI) cung cấp dịch vụ tư vấn t&agrave;i ch&iacute;nh vượt trội d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nguồn tiền nh&agrave;n rỗi, muốn đầu tư sinh lời nhưng kh&ocirc;ng đủ thời gian để tự nghi&ecirc;n cứu. Thay v&igrave; gửi tiền ng&acirc;n h&agrave;ng với l&atilde;i suất chỉ 6-7%/năm, kh&aacute;ch h&agrave;ng c&oacute; thể gửi tiền v&agrave;o quỹ chung tại PI để c&aacute;c chuy&ecirc;n gia thực hiện đầu tư chứng kho&aacute;n v&agrave; thu về mức lợi nhuận gấp 2 -3 lần. Đ&acirc;y l&agrave; một h&igrave;nh thức đầu tư kh&aacute; mới ở Việt Nam nhưng đ&atilde; xuất hiện gần 100 năm tại c&aacute;c thị trường kh&aacute;c. Tại Mỹ, hơn một nửa số hộ gia đ&igrave;nh c&oacute; đầu tư v&agrave;o c&aacute;c c&ocirc;ng ty tương tự như m&ocirc; h&igrave;nh hoạt động của PI.</p>\r\n\r\n<p>Kể từ thời điểm bắt đầu hoạt động th&aacute;ng 1/2016 đến th&aacute;ng 3/2018, Passion Investment đ&atilde;&nbsp;cho thấy hiệu quả đầu tư đ&aacute;ng kinh ngạc l&ecirc;n đến 273%. Uy t&iacute;n v&agrave; vị thế của Passion Investment&nbsp;kh&ocirc;ng ngừng&nbsp;được n&acirc;ng cao. Số kh&aacute;ch h&agrave;ng t&iacute;nh đến th&aacute;ng 3/2018 l&agrave; 700 kh&aacute;ch h&agrave;ng với hơn 2000 hợp đồng được k&yacute; kết c&ugrave;ng tổng gi&aacute; trị t&agrave;i sản quản l&yacute; l&ecirc;n đến hơn 800 tỷ đồng. Passion Investment tự h&agrave;o l&agrave; một trong số &iacute;t c&aacute;c doanh nghiệp Việt Nam đi ti&ecirc;n phong trong trong lĩnh vực đầu tư t&agrave;i ch&iacute;nh, đầu tư chứng kho&aacute;n chuy&ecirc;n nghiệp.</p>', '0', '0', 0, '2018-03-23 08:48:11', 0, 0, '2018-03-23 08:48:11', '2018-03-23 08:48:11', '2018-03-23 08:48:11', 'recruitments/LbftHqsuPLTxsZSrkZrdeMbPV04Qu2GQ8hu5U9PL.png', 'tuyển dụng', 0, 'tuyển dụng, lễ tân', 'tuyển dụng', 0, 0, 'vietnam', 'Tuyển dụng nhân viên Lễ tân', NULL, '2018-03-23 01:48:11', '2023-02-24 00:47:45'),
(3, 'Tuyển dụng vị trí Content Marketing – Passion Investment', 'tuyen-dung-vi-tri-content-marketing-passion-investment-2', 'Quyền lợi cho vị trí Content marketing:\r\n– Lương cứng 7-10 triệu (Có thể thỏa thuận thêm) thưởng hiệu quả công việc;\r\n– Lương tháng thứ 13, làm việc từ 8h đến 17h30, thứ 2 đến thứ 6, (Nghỉ T7, CN);\r\n………..', '<p><strong>BẠN SẼ L&Agrave;M G&Igrave;?</strong><br />\r\n<br />\r\n&ndash; L&ecirc;n kế hoạch v&agrave; trực tiếp triển khai nội dung cho c&aacute;c k&ecirc;nh truyền th&ocirc;ng của c&ocirc;ng ty dưới dạng b&agrave;i text, h&igrave;nh ảnh, video đơn giản,&hellip;</p>\r\n\r\n<p>&ndash; Quản trị fanpage, viết c&aacute;c b&agrave;i đăng fanpage, website;</p>\r\n\r\n<p>&ndash; Viết b&agrave;i SEO, b&agrave;i PR, tổng hợp tin tức;</p>\r\n\r\n<p>&ndash; Đề xuất c&aacute;c &yacute; tưởng l&agrave;m nội dung cho video;</p>\r\n\r\n<p>&ndash; Theo d&otilde;i, tổng hợp tin tức, sự kiện li&ecirc;n quan đến ng&agrave;nh;</p>\r\n\r\n<p>&ndash; Một số c&ocirc;ng việc theo sự ph&acirc;n c&ocirc;ng của quản l&yacute;.</p>\r\n\r\n<p><strong>BẠN CẦN Đ&Aacute;P ỨNG Y&Ecirc;U CẦU G&Igrave;?</strong><br />\r\n&ndash; C&oacute;&nbsp;khoảng&nbsp;1 năm kinh nghiệm l&agrave;m việc tại vị tr&iacute; tương đương, ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; kinh nghiệm trong ng&agrave;nh ng&acirc;n h&agrave;ng, t&agrave;i ch&iacute;nh;<br />\r\n&ndash; C&oacute; đam m&ecirc; với lĩnh vực s&aacute;ng tạo nội dung;<br />\r\n&ndash; C&oacute; khả năng chịu được &aacute;p lực, nhiệt t&igrave;nh s&aacute;ng tạo trong c&ocirc;ng việc, l&agrave;m việc theo nh&oacute;m;<br />\r\n&ndash;&nbsp;Ưu ti&ecirc;n ứng vi&ecirc;n biết sử dụng phần mềm đồ hoạ v&agrave; chỉnh sửa video;<br />\r\n&ndash; Chủ động trong việc điều phối, triển khai c&ocirc;ng việc.</p>\r\n\r\n<p><strong>BẠN SẼ NHẬN ĐƯỢC QUYỀN LỢI G&Igrave;?</strong><br />\r\n&ndash; Lương cố định 7-10 triệu (C&oacute; thể thỏa thuận th&ecirc;m) thưởng hiệu quả c&ocirc;ng việc;<br />\r\n&ndash; M&ocirc;i trường l&agrave;m việc trẻ, năng động,&nbsp;C&oacute; laptop ri&ecirc;ng, đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n m&ocirc;n;<br />\r\n&ndash; Lương th&aacute;ng thứ 13;&nbsp;Du lịch hằng năm;<br />\r\n&ndash; Hưởng đầy đủ c&aacute;c chế độ BHXH, BHYT, BHTN<br />\r\n&ndash; Thời gian l&agrave;m việc: 8h đến 17h30 từ thứ 2 đến thứ 6. Nghỉ thứ 7 + chủ nhật;<br />\r\n&ndash; Tham gia c&aacute;c kh&oacute;a đ&agrave;o tạo n&acirc;ng cao kĩ năng về marketing online;<br />\r\n&ndash; Thăng tiến l&ecirc;n vị tr&iacute; quản l&yacute;.<br />\r\n&ndash; Số lượng cần tuyển: 4 content marketing;</p>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 26, T&ograve;a nh&agrave; Mipec, Số 229 T&acirc;y Sơn, Quận Đống Đa, H&agrave; Nội</p>\r\n\r\n<p><strong>Ứng tuyển ngay bằng c&aacute;ch gửi CV về email:&nbsp;nhansu</strong><strong>@pif.vn</strong></p>\r\n\r\n<p><strong>Để biết th&ecirc;m chi tiết vui l&ograve;ng li&ecirc;n hệ:&nbsp;</strong>(024) 32 646 480</p>\r\n\r\n<p><strong>Hoặc truy cập website:</strong>&nbsp;<a href=\"https://old.pif.vn/\"><strong>https://old.pif.vn/</strong></a></p>\r\n\r\n<h3>GIỚI THIỆU VỀ C&Ocirc;NG TY</h3>\r\n\r\n<p>C&ocirc;ng ty Cổ phần Tư vấn đầu tư Passion Investment (PI) cung cấp dịch vụ tư v&acirc;́n t&agrave;i ch&iacute;nh vượt trội d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nguồn tiền nh&agrave;n rỗi, muốn đầu tư sinh lời nhưng kh&ocirc;ng đủ thời gian để tự nghi&ecirc;n cứu. Thay vì gửi ti&ecirc;̀n ng&acirc;n hàng với lãi su&acirc;́t chỉ 6-7%/năm, khách hàng có th&ecirc;̉ gửi ti&ecirc;̀n vào quỹ chung tại PI đ&ecirc;̉ các chuy&ecirc;n gia thực hi&ecirc;̣n đ&acirc;̀u tư chứng khoán và thu v&ecirc;̀ mức lợi nhu&acirc;̣n g&acirc;́p 2 -3 l&acirc;̀n. Đ&acirc;y là m&ocirc;̣t hình thức đ&acirc;̀u tư khá mới ở Vi&ecirc;̣t Nam nhưng đã xu&acirc;́t hi&ecirc;̣n g&acirc;̀n 100 năm tại các thị trường khác. Tại Mỹ, hơn m&ocirc;̣t nửa s&ocirc;́ h&ocirc;̣ gia đình có đ&acirc;̀u tư vào các c&ocirc;ng ty tương tự như m&ocirc; hình hoạt đ&ocirc;̣ng của PI.</p>\r\n\r\n<p>Kể từ thời điểm bắt đầu hoạt động th&aacute;ng 1/2016 đến th&aacute;ng 3/2018, Passion Investment đ&atilde;&nbsp;cho thấy hiệu quả đầu tư đ&aacute;ng kinh ngạc l&ecirc;n đến 273%. Uy t&iacute;n v&agrave; vị thế của Passion Investment&nbsp;kh&ocirc;ng ngừng&nbsp;được n&acirc;ng cao. Số kh&aacute;ch h&agrave;ng t&iacute;nh đến th&aacute;ng 3/2018 l&agrave; 800 kh&aacute;ch h&agrave;ng với hơn 2000 hợp đồng được k&yacute; kết c&ugrave;ng tổng gi&aacute; trị t&agrave;i sản quản l&yacute; l&ecirc;n đến hơn 800 tỷ đồng. Passion Investment tự h&agrave;o l&agrave; một trong số &iacute;t c&aacute;c doanh nghiệp Việt Nam đi ti&ecirc;n phong trong trong lĩnh vực đầu tư t&agrave;i ch&iacute;nh, đầu tư chứng kho&aacute;n chuy&ecirc;n nghiệp.</p>', '0', '0', 0, '2018-04-12 09:12:21', 0, 0, '2018-04-12 09:12:21', '2018-04-12 09:12:21', '2018-04-12 09:12:21', 'recruitments/nogisTm1vEfk3wRBJ1iuOWon86449GwqWXpdjr83.jpg', 'tin tức, tuyển dụng', 0, 'tin tức, tuyển dụng, content marketing', 'tuyển dụng', 0, 0, 'vietnam', 'hết hạn', NULL, '2018-04-12 02:12:21', '2023-02-24 00:47:24'),
(4, 'Tuyển dụng vị trí Nhân viên Hành Chính', 'tuyen-dung-vi-tri-nhan-vien-hanh-chinh', 'Quyền lợi cho vị trí Nhân viên hành chính:\r\n– Lương cứng 6-10 triệu (Có thể thỏa thuận thêm) thưởng hiệu quả công việc;\r\n– Lương tháng thứ 13, làm việc từ 8h đến 17h30, thứ 2 đến thứ 6, (Nghỉ T7, CN);\r\n………..', '<ol>\r\n	<li>\r\n	<h3><strong>1.M&ocirc; t</strong><strong>ả</strong><strong>&nbsp;c&ocirc;ng vi</strong><strong>ệ</strong><strong>c:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Chịu tr&aacute;ch nhiệm l&agrave;m b&aacute;o c&aacute;o h&agrave;ng ng&agrave;y/h&agrave;ng tuần cho Bộ phận c&oacute; li&ecirc;n quan, Ban quản l&yacute; v&agrave; Kh&aacute;ch h&agrave;ng;</li>\r\n	<li>Soạn thảo văn bản h&agrave;nh ch&iacute;nh khi c&oacute; ph&aacute;t sinh;</li>\r\n	<li>Hỗ trợ ph&ograve;ng Chăm s&oacute;c kh&aacute;ch h&agrave;ng ho&agrave;n thiện c&aacute;c thủ tục li&ecirc;n quan đến kh&aacute;ch h&agrave;ng;</li>\r\n	<li>Hỗ trợ c&aacute;c thủ tục giấy tờ li&ecirc;n quan đến b&ecirc;n thứ 3;</li>\r\n	<li>C&aacute;c c&ocirc;ng việc kh&aacute;c theo y&ecirc;u cầu của Quản l&yacute;.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><strong>Ch</strong><strong>ế</strong><strong>&nbsp;đ</strong><strong>ộ</strong><strong>&nbsp;v&agrave; c&aacute;c quy</strong><strong>ề</strong><strong>n l</strong><strong>ợ</strong><strong>i:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Lương cơ bản từ 6-10 triệu, lương th&aacute;ng thứ 13 + thưởng cuối năm theo hiệu quả c&ocirc;ng việc;</li>\r\n	<li>M&ocirc;i trường l&agrave;m việc trẻ, năng động, nhiều hoạt động văn h&oacute;a s&ocirc;i nổi quan t&acirc;m đến đời sống tinh thần của nh&acirc;n vi&ecirc;n v&agrave; được đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n nghiệp;</li>\r\n	<li>Du lịch 2 lần/năm, Sinh nhật, Lễ tết;</li>\r\n	<li>L&agrave;m việc từ thứ 2 &ndash; 6 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN;</li>\r\n	<li>C&oacute; cơ hội thăng tiến trong c&ocirc;ng việc.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><strong>Y&ecirc;u c</strong><strong>ầ</strong><strong>u c&ocirc;ng vi</strong><strong>ệ</strong><strong>c:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tốt nghiệp Đại học khối ngh&agrave;nh kinh tế, t&agrave;i ch&iacute;nh;</li>\r\n	<li>Phẩm chất c&aacute; nh&acirc;n: kỹ năng giao tiếp tốt, nhiệt huyết, sẵn s&agrave;ng học hỏi v&agrave; c&oacute; khả năng chịu được &aacute;p lực cao, cẩn thận, tỉ mỉ.</li>\r\n	<li>C&oacute; kỹ năng l&agrave;m việc với c&aacute;c b&ecirc;n thứ 3, c&aacute;c nh&agrave; cung cấp.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute;&nbsp; kinh nghiệm l&agrave;m việc;\r\n	<h3>GIỚI THIỆU VỀ C&Ocirc;NG TY</h3>\r\n\r\n	<p>C&ocirc;ng ty Cổ phần Tư vấn đầu tư Passion Investment (PI) cung cấp dịch vụ tư v&acirc;́n t&agrave;i ch&iacute;nh vượt trội d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nguồn tiền nh&agrave;n rỗi, muốn đầu tư sinh lời nhưng kh&ocirc;ng đủ thời gian để tự nghi&ecirc;n cứu. Thay vì gửi ti&ecirc;̀n ng&acirc;n hàng với lãi su&acirc;́t chỉ 6-7%/năm, khách hàng có th&ecirc;̉ gửi ti&ecirc;̀n vào quỹ chung tại PI đ&ecirc;̉ các chuy&ecirc;n gia thực hi&ecirc;̣n đ&acirc;̀u tư chứng khoán và thu v&ecirc;̀ mức lợi nhu&acirc;̣n g&acirc;́p 2 -3 l&acirc;̀n. Đ&acirc;y là m&ocirc;̣t hình thức đ&acirc;̀u tư khá mới ở Vi&ecirc;̣t Nam nhưng đã xu&acirc;́t hi&ecirc;̣n g&acirc;̀n 100 năm tại các thị trường khác. Tại Mỹ, hơn m&ocirc;̣t nửa s&ocirc;́ h&ocirc;̣ gia đình có đ&acirc;̀u tư vào các c&ocirc;ng ty tương tự như m&ocirc; hình hoạt đ&ocirc;̣ng của PI.</p>\r\n\r\n	<p>Kể từ thời điểm bắt đầu hoạt động th&aacute;ng 1/2016 đến th&aacute;ng 3/2018, Passion Investment đ&atilde;&nbsp;cho thấy hiệu quả đầu tư đ&aacute;ng kinh ngạc l&ecirc;n đến 273%. Uy t&iacute;n v&agrave; vị thế của Passion Investment&nbsp;kh&ocirc;ng ngừng&nbsp;được n&acirc;ng cao. Số kh&aacute;ch h&agrave;ng t&iacute;nh đến th&aacute;ng 3/2018 l&agrave; 800 kh&aacute;ch h&agrave;ng với hơn 2000 hợp đồng được k&yacute; kết c&ugrave;ng tổng gi&aacute; trị t&agrave;i sản quản l&yacute; l&ecirc;n đến hơn 800 tỷ đồng. Passion Investment tự h&agrave;o l&agrave; một trong số &iacute;t c&aacute;c doanh nghiệp Việt Nam đi ti&ecirc;n phong trong trong lĩnh vực đầu tư t&agrave;i ch&iacute;nh, đầu tư chứng kho&aacute;n chuy&ecirc;n nghiệp.</p>\r\n	</li>\r\n</ul>', '0', '0', 0, '2018-04-28 09:09:21', 0, 0, '2018-04-28 09:09:21', '2018-04-28 09:09:21', '2018-04-28 09:09:21', 'recruitments/RunOGu7BjlfcRlMEfgUlrNnrkUxS3JKqcRfl61S9.jpg', 'Tin tức, Tuyển dụng', 0, 'Tin tức, Tuyển dụng, NVHC', 'Tuyển dụng', 0, 0, 'vietnam', 'hết hạn', NULL, '2018-04-28 02:09:21', '2023-02-24 00:47:07'),
(5, 'Passion Investment tuyển dụng thực tập phân tích đầu tư', 'passion-investment-tuyen-dung-thuc-tap-phan-tich-dau-tu', 'Bạn đang là sinh viên nhưng có niềm đam mê với lĩnh vực đầu tư, muốn tìm hiểu về thị trường chứng khoán? Bạn muốn tham gia những buổi tranh luận sôi nổi về đầu tư cùng các chuyên gia hàng đầu?', '<p>Bạn đang l&agrave; sinh vi&ecirc;n nhưng c&oacute; niềm đam m&ecirc; với lĩnh vực đầu tư, muốn t&igrave;m hiểu về thị trường chứng kho&aacute;n? Bạn muốn tham gia những buổi tranh luận s&ocirc;i nổi về đầu tư c&ugrave;ng c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu?</p>\r\n\r\n<p>Passion Investment ch&iacute;nh l&agrave; lựa chọn ho&agrave;n hảo cho bạn với chương tr&igrave;nh Internship đầy th&aacute;ch thức khi bạn được l&agrave;m việc, va chạm thực tế khốc liệt trong lĩnh vực đầu tư v&agrave; được hướng dẫn bởi c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu trong lĩnh vực n&agrave;y.</p>\r\n\r\n<p>Văn ph&ograve;ng c&ocirc;ng ty đặt tại tầng cao nhất của t&ograve;a nh&agrave; MIPEC &ndash; 229 T&acirc;y Sơn với tầm nh&igrave;n bao qu&aacute;t to&agrave;n H&agrave; Nội c&ugrave;ng với đ&oacute; l&agrave; cơ sở vật chất hiện đại gồm khu văn ph&ograve;ng, bếp ăn, khu ph&ograve;ng họp, ph&ograve;ng kh&aacute;ch, kh&ocirc;ng kh&aacute;c g&igrave; những Co-working Space l&agrave; địa điểm l&yacute; tưởng gi&uacute;p bạn ph&aacute;t huy khả năng tư duy v&agrave; s&aacute;ng tạo của m&igrave;nh.</p>\r\n\r\n<p><strong>1. M&ocirc; tả c&ocirc;ng việc:</strong><br />\r\n&ndash; Trực ti&ecirc;́p ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute;, định gi&aacute; m&ocirc;̣t s&ocirc;́ cơ hội đầu tư được giao;<br />\r\n&ndash; Theo d&otilde;i thị trường, cập nhật th&ocirc;ng tin của c&aacute;c doanh nghiệp đ&atilde; đầu tư, đưa ra c&aacute;c đề xuất li&ecirc;n quan đến việc ra quyết định đầu tư của c&ocirc;ng ty;<br />\r\n&ndash; Thực hiện vi&ecirc;̣c tìm ki&ecirc;́m v&agrave; đ&aacute;nh gi&aacute; các cơ h&ocirc;̣i đ&acirc;̀u tư mới.</p>\r\n\r\n<p><strong>2. Quyền lợi</strong><br />\r\n&ndash; Được đ&agrave;o tạo kỹ năng ph&acirc;n t&iacute;ch b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh, nghi&ecirc;n cứu thị trường, nghi&ecirc;n cứu doanh nghiệp,&hellip;<br />\r\n&ndash; Training về c&aacute;c phương ph&aacute;p đầu tư b&agrave;i bản, chuy&ecirc;n nghiệp từ c&aacute;c chuy&ecirc;n gia nhiều năm kinh nghiệm trong lĩnh vực đầu tư t&agrave;i ch&iacute;nh;<br />\r\n&ndash; Hướng dẫn c&aacute;c bước cơ bản để ph&acirc;n t&iacute;ch một cơ hội đầu tư;<br />\r\n&ndash; Trực tiếp tham gia v&agrave;o qu&aacute; tr&igrave;nh t&igrave;m kiếm cũng như nghi&ecirc;n cứu đầu tư cổ phiếu;<br />\r\n&ndash; Tham gia c&aacute;c buổi họp, thuyết tr&igrave;nh, brainstorm, debate,&hellip; về c&aacute;c &ldquo;deal&rdquo; đầu tư lớn;<br />\r\n&ndash; Cơ hội trải nghiệm m&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp của một c&ocirc;ng ty đầu tư;<br />\r\n&ndash; Cơ hội trở th&agrave;nh nh&acirc;n vi&ecirc;n ch&iacute;nh thức của c&ocirc;ng ty;<br />\r\n&ndash; Thời gian linh hoạt, chủ động sắp xếp theo thời gian c&aacute; nh&acirc;n.</p>\r\n\r\n<p><strong>3. Y&ecirc;u cầu:</strong><br />\r\n&ndash; Sinh vi&ecirc;n năm 3-4 c&aacute;c trường Đại học, sinh vi&ecirc;n đ&atilde; tốt nghiệp;<br />\r\n&ndash; C&oacute; kiến thức cơ bản về t&agrave;i ch&iacute;nh l&agrave; một lợi thế;<br />\r\n&ndash; C&oacute; đam m&ecirc; về thị trường chứng kho&aacute;n, đầu tư.</p>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 26 , to&agrave; Mipec 229 T&acirc;y Sơn, Đống Đa, H&agrave; Nội.<br />\r\nỨng tuyển vui l&ograve;ng gửi mail về: dichvu@pif.vn hoặc nhansu@pif.vn<br />\r\nTi&ecirc;u đề ghi r&otilde;: T&ecirc;n &ndash; Vị tr&iacute; ứng tuyển</p>\r\n\r\n<h3>GIỚI THIỆU VỀ C&Ocirc;NG TY</h3>\r\n\r\n<p>C&ocirc;ng ty Cổ phần Tư vấn đầu tư Passion Investment (PI) cung cấp dịch vụ tư v&acirc;́n t&agrave;i ch&iacute;nh vượt trội d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nguồn tiền nh&agrave;n rỗi, muốn đầu tư sinh lời nhưng kh&ocirc;ng đủ thời gian để tự nghi&ecirc;n cứu. Thay vì gửi ti&ecirc;̀n ng&acirc;n hàng với lãi su&acirc;́t chỉ 6-7%/năm, khách hàng có th&ecirc;̉ gửi ti&ecirc;̀n vào quỹ chung tại PI đ&ecirc;̉ các chuy&ecirc;n gia thực hi&ecirc;̣n đ&acirc;̀u tư chứng khoán và thu v&ecirc;̀ mức lợi nhu&acirc;̣n g&acirc;́p 2 -3 l&acirc;̀n. Đ&acirc;y là m&ocirc;̣t hình thức đ&acirc;̀u tư khá mới ở Vi&ecirc;̣t Nam nhưng đã xu&acirc;́t hi&ecirc;̣n g&acirc;̀n 100 năm tại các thị trường khác. Tại Mỹ, hơn m&ocirc;̣t nửa s&ocirc;́ h&ocirc;̣ gia đình có đ&acirc;̀u tư vào các c&ocirc;ng ty tương tự như m&ocirc; hình hoạt đ&ocirc;̣ng của PI.</p>\r\n\r\n<p>Kể từ thời điểm bắt đầu hoạt động th&aacute;ng 1/2016 đến th&aacute;ng 3/2018, Passion Investment đ&atilde;&nbsp;cho thấy hiệu quả đầu tư đ&aacute;ng kinh ngạc l&ecirc;n đến 273%. Uy t&iacute;n v&agrave; vị thế của Passion Investment&nbsp;kh&ocirc;ng ngừng&nbsp;được n&acirc;ng cao. Số kh&aacute;ch h&agrave;ng t&iacute;nh đến th&aacute;ng 3/2018 l&agrave; 800 kh&aacute;ch h&agrave;ng với hơn 2000 hợp đồng được k&yacute; kết c&ugrave;ng tổng gi&aacute; trị t&agrave;i sản quản l&yacute; l&ecirc;n đến hơn 800 tỷ đồng. Passion Investment tự h&agrave;o l&agrave; một trong số &iacute;t c&aacute;c doanh nghiệp Việt Nam đi ti&ecirc;n phong trong trong lĩnh vực đầu tư t&agrave;i ch&iacute;nh, đầu tư chứng kho&aacute;n chuy&ecirc;n nghiệp.</p>', '0', '0', 0, '2018-08-10 09:12:21', 0, 0, '2018-08-10 09:12:21', '2018-08-10 09:12:21', '2018-08-10 09:12:21', 'recruitments/ewQlLCXg5ZqGyBKrSLVtwGWVWcVKhAixGoyda3KS.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Phân tích, Đầu tư', 'thực tập', 0, 0, 'vietnam', 'hết hạn', NULL, '2018-08-10 02:12:21', '2023-02-24 00:46:42'),
(6, 'Tuyển dụng vị trí Nhân viên Hành chính – Passion Investment', 'tuyen-dung-vi-tri-nhan-vien-hanh-chinh-passion-investment', '1.Mô tả công việc: Chịu trách nhiệm làm báo cáo hàng ngày/hàng tuần cho Bộ phận có liên quan, Ban quản lý và Khách hàng; Soạn thảo văn bản hành', '<ol>\r\n	<li>\r\n	<h3><strong>1.M&ocirc; t</strong><strong>ả</strong><strong>&nbsp;c&ocirc;ng vi</strong><strong>ệ</strong><strong>c:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Chịu tr&aacute;ch nhiệm l&agrave;m b&aacute;o c&aacute;o h&agrave;ng ng&agrave;y/h&agrave;ng tuần cho Bộ phận c&oacute; li&ecirc;n quan, Ban quản l&yacute; v&agrave; Kh&aacute;ch h&agrave;ng;</li>\r\n	<li>Soạn thảo văn bản h&agrave;nh ch&iacute;nh khi c&oacute; ph&aacute;t sinh;</li>\r\n	<li>Hỗ trợ ph&ograve;ng Chăm s&oacute;c kh&aacute;ch h&agrave;ng ho&agrave;n thiện c&aacute;c thủ tục li&ecirc;n quan đến kh&aacute;ch h&agrave;ng;</li>\r\n	<li>Hỗ trợ c&aacute;c thủ tục giấy tờ li&ecirc;n quan đến b&ecirc;n thứ 3;</li>\r\n	<li>C&aacute;c c&ocirc;ng việc kh&aacute;c theo y&ecirc;u cầu của Quản l&yacute;.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><strong>Ch</strong><strong>ế</strong><strong>&nbsp;đ</strong><strong>ộ</strong><strong>&nbsp;v&agrave; c&aacute;c quy</strong><strong>ề</strong><strong>n l</strong><strong>ợ</strong><strong>i:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Lương cơ bản từ 8-10 triệu, lương th&aacute;ng thứ 13 + thưởng cuối năm theo hiệu quả c&ocirc;ng việc;</li>\r\n	<li>M&ocirc;i trường l&agrave;m việc trẻ, năng động, nhiều hoạt động văn h&oacute;a s&ocirc;i nổi quan t&acirc;m đến đời sống tinh thần của nh&acirc;n vi&ecirc;n v&agrave; được đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n nghiệp;</li>\r\n	<li>Du lịch 2 lần/năm, Sinh nhật, Lễ tết;</li>\r\n	<li>L&agrave;m việc từ thứ 2 &ndash; 6 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN;</li>\r\n	<li>C&oacute; cơ hội thăng tiến trong c&ocirc;ng việc.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><strong>Y&ecirc;u c</strong><strong>ầ</strong><strong>u c&ocirc;ng vi</strong><strong>ệ</strong><strong>c:</strong></h3>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Sử dụng th&agrave;nh thạo tiếng Trung (Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; thể sử dụng tiếng Anh)</li>\r\n	<li>Phẩm chất c&aacute; nh&acirc;n: kỹ năng giao tiếp tốt, nhiệt huyết, sẵn s&agrave;ng học hỏi v&agrave; c&oacute; khả năng chịu được &aacute;p lực cao, cẩn thận, tỉ mỉ.</li>\r\n	<li>C&oacute; kỹ năng l&agrave;m việc với c&aacute;c b&ecirc;n thứ 3, c&aacute;c nh&agrave; cung cấp.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute; kinh nghiệm l&agrave;m việc;</li>\r\n</ul>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 26, T&ograve;a nh&agrave; Mipec, Số 229 T&acirc;y Sơn, Quận Đống Đa, H&agrave; Nội</p>\r\n\r\n<p><strong>Ứng tuyển ngay bằng c&aacute;ch gửi CV về email:&nbsp;nhansu</strong><strong>@pif.vn</strong></p>\r\n\r\n<p><strong>Để biết th&ecirc;m chi tiết vui l&ograve;ng li&ecirc;n hệ:&nbsp;</strong>(024) 32 646 480</p>\r\n\r\n<p><strong>Hoặc truy cập website:</strong>&nbsp;<a href=\"https://old.pif.vn/\"><strong>https://old.pif.vn/</strong></a></p>', '0', '0', 0, '2018-11-05 09:12:21', 0, 0, '2018-11-05 09:12:21', '2018-11-05 09:12:21', '2018-11-05 09:12:21', 'recruitments/ZaZn5quUdK4bqMkYndd7ID5h0STkA7Pj3pVekmYB.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, NVHC', 'Tuyển dụng', 0, 0, 'vietnam', 'hết hạn', NULL, '2018-11-05 02:12:21', '2023-02-24 00:44:45'),
(7, 'Tuyển dụng vị trí Chuyên viên CSKH – Passion Investment', 'tuyen-dung-vi-tri-chuyen-vien-cskh-passion-investment', '1. Mô tả công việc: • Tiếp nhận chăm sóc các khách hàng hiện tại của công ty qua các kênh email, điện thoại. • Phối hợp với bên thứ', '<h3>1. M&ocirc; tả c&ocirc;ng việc:</h3>\r\n\r\n<p>&bull; Ti&ecirc;́p nh&acirc;̣n chăm sóc các khách hàng hi&ecirc;̣n tại của c&ocirc;ng ty qua các k&ecirc;nh email, đi&ecirc;̣n thoại.<br />\r\n&bull; Phối hợp với b&ecirc;n thứ 3 trong qu&aacute; tr&igrave;nh hỗ trợ hồ sơ thủ tục của kh&aacute;ch h&agrave;ng.<br />\r\n&bull; Ti&ecirc;́p nh&acirc;̣n, trả lời, tư vấn khách hàng v&ecirc;̀ sản ph&acirc;̉m, dịch vụ.<br />\r\n&bull; Quản trị website, fanpage, email.<br />\r\n&bull; Đ&ecirc;̀ xu&acirc;́t, x&acirc;y dựng các hoạt đ&ocirc;̣ng chăm sóc khách hàng.<br />\r\n&bull; Y&ecirc;u c&acirc;̀u phát sinh khác do cán b&ocirc;̣ quản lý giao.</p>\r\n\r\n<h3>2. Y&ecirc;u cầu c&ocirc;ng việc:</h3>\r\n\r\n<p>&bull; Phẩm chất c&aacute; nh&acirc;n: kỹ năng giao tiếp tốt, nhiệt huyết, trung thực, c&oacute; tinh thần hợp t&aacute;c v&agrave; sẵn s&agrave;ng học hỏi trong c&ocirc;ng việc.<br />\r\n&bull; Th&agrave;nh thạo MS Office.<br />\r\n&bull; Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute; kinh nghiệm l&agrave;m việc;<br />\r\n&bull; Kh&ocirc;ng y&ecirc;u cầu bằng cấp.</p>\r\n\r\n<h3>3. Chế độ v&agrave; c&aacute;c quyền lợi:</h3>\r\n\r\n<p>&bull; Mức lương: thỏa thuận + thưởng cuối năm theo hiệu quả c&ocirc;ng việc v&agrave; c&aacute;c dịp lễ , Tết;<br />\r\n&bull; Du lịch trong v&agrave; ngo&agrave;i nước;<br />\r\n&bull; M&ocirc;i trường l&agrave;m việc trẻ, năng động, nhiều hoạt động văn h&oacute;a s&ocirc;i nổi quan t&acirc;m đến đời sống tinh thần của nh&acirc;n vi&ecirc;n v&agrave; được đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n nghiệp;<br />\r\n&bull; L&agrave;m việc từ thứ 2 &ndash; 6 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN;</p>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 6, Số 1 Nh&agrave; D- 181 Nguyễn Lương Bằng, Quận Đống Đa, H&agrave; Nội.<br />\r\n<strong>Ứng tuyển ngay bằng c&aacute;ch gửi CV về email: dichvu@pif.vn</strong><br />\r\n<strong>Để biết th&ecirc;m chi tiết vui l&ograve;ng li&ecirc;n hệ:</strong>&nbsp;(024) 32 646 480.<br />\r\n<strong>Hoặc truy cập website</strong>: https://old.pif.vn/</p>', '0', '0', 0, '2019-03-15 09:09:21', 0, 0, '2019-03-15 09:09:21', '2019-03-15 09:09:21', '2019-03-15 09:09:21', 'recruitments/CSezaUwABlXZSaan4RhqD8v5x5YeRBSENCslt2c1.png', 'Tuyển dụng', 0, 'Tuyển dụng, Chuyên viên, CSKH', 'Chuyên viên', 0, 0, 'vietnam', 'hết hạn', NULL, '2019-03-15 02:09:21', '2023-02-24 00:44:25'),
(8, 'Tuyển dụng vị trí Nhân viên Hành chính – Passion Investment', 'tuyen-dung-vi-tri-nhan-vien-hanh-chinh-passion-investment-2', '1. Mô tả công việc: Soạn thảo văn bản hành chính khi có phát sinh; Hỗ trợ phòng Chăm sóc khách hàng hoàn thiện các thủ tục liên quan đến khách', '<p><strong>1. M&ocirc; tả&nbsp;c&ocirc;ng việc:</strong></p>\r\n\r\n<ul>\r\n	<li>Soạn thảo văn bản h&agrave;nh ch&iacute;nh khi c&oacute; ph&aacute;t sinh;</li>\r\n	<li>Hỗ trợ ph&ograve;ng Chăm s&oacute;c kh&aacute;ch h&agrave;ng ho&agrave;n thiện c&aacute;c thủ tục li&ecirc;n quan đến kh&aacute;ch h&agrave;ng;</li>\r\n	<li>Hỗ trợ c&aacute;c thủ tục giấy tờ li&ecirc;n quan đến b&ecirc;n thứ 3;</li>\r\n	<li>C&aacute;c c&ocirc;ng việc kh&aacute;c theo y&ecirc;u cầu của Quản l&yacute;.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>2. Y&ecirc;u cầu c&ocirc;ng việc:</strong></p>\r\n\r\n<ul>\r\n	<li>Phẩm chất c&aacute; nh&acirc;n: nhiệt t&igrave;nh, trung thực, cẩn thận c&oacute; tinh thần hợp t&aacute;c v&agrave; sẵn s&agrave;ng học hỏi trong c&ocirc;ng việc.</li>\r\n	<li>C&oacute; kỹ năng l&agrave;m việc với c&aacute;c b&ecirc;n thứ 3, c&aacute;c nh&agrave; cung cấp.</li>\r\n	<li>Th&agrave;nh thạo MS Office.</li>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute; kinh nghiệm l&agrave;m việc;</li>\r\n	<li>Kh&ocirc;ng y&ecirc;u cầu bằng cấp</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>3. Chế&nbsp;độ&nbsp;v&agrave; c&aacute;c quyền lợi:</strong></p>\r\n\r\n<ul>\r\n	<li>Mức lương: thỏa thuận + thưởng cuối năm theo hiệu quả c&ocirc;ng việc v&agrave; c&aacute;c dịp lễ , Tết;</li>\r\n	<li>Du lịch trong v&agrave; ngo&agrave;i nước;</li>\r\n	<li>M&ocirc;i trường l&agrave;m việc trẻ, năng động, nhiều hoạt động văn h&oacute;a s&ocirc;i nổi quan t&acirc;m đến đời sống tinh thần của nh&acirc;n vi&ecirc;n v&agrave; được đ&agrave;o tạo ph&aacute;t triển chuy&ecirc;n nghiệp;</li>\r\n	<li>L&agrave;m việc từ thứ 2 &ndash; 6 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN;</li>\r\n</ul>', '0', '0', 0, '2019-03-15 09:21:49', 0, 0, '2019-03-15 09:21:49', '2019-03-15 09:21:49', '2019-03-15 09:21:49', 'recruitments/E8pP1dFuoVSBIZ0vz7hReEbZwp7D5t7IgFj1y6Xw.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, NVHC, Passion Investment', 'NVHC', 0, 0, 'vietnam', 'hết hạn', NULL, '2019-03-15 02:21:49', '2023-02-24 00:44:03'),
(9, 'Tuyển dụng nhân viên Phân tích Kỹ thuật – Passion Investment', 'tuyen-dung-nhan-vien-phan-tich-ky-thuat-passion-investment', 'Mô tả công việc Phụ trách phân tích kỹ thuật về thị trường chứng khoán và các cổ phiếu được yêu cầu. Đề xuất ý tưởng đầu tư dựa trên', '<ol>\r\n	<li><strong>M&ocirc; tả c&ocirc;ng việc</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Phụ tr&aacute;ch ph&acirc;n t&iacute;ch kỹ thuật về thị trường chứng kho&aacute;n v&agrave; c&aacute;c cổ phiếu được y&ecirc;u cầu.</li>\r\n	<li>Đề xuất &yacute; tưởng đầu tư dựa tr&ecirc;n ph&acirc;n t&iacute;ch kỹ thuật, theo d&otilde;i c&aacute;c khoản đầu tư được duyệt.</li>\r\n	<li>Tham gia v&agrave;o qu&aacute; tr&igrave;nh đầu tư v&agrave; quản l&yacute; danh mục.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><strong>Y&ecirc;u cầu c&ocirc;ng việc</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, kinh tế, đầu tư, chứng kho&aacute;n.</li>\r\n	<li>C&oacute; kiến thức về ph&acirc;n t&iacute;ch kỹ thuật v&agrave; đ&atilde; c&oacute; kinh nghiệm ứng dụng thực tiễn.</li>\r\n	<li>Sử dụng th&agrave;nh thạo c&aacute;c phần mềm ph&acirc;n t&iacute;ch kỹ thuật.</li>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; chứng chỉ về ph&acirc;n t&iacute;ch kỹ thuật (CMT) hoặc đ&atilde; từng c&oacute; c&oacute; kinh nghiệm l&agrave;m Ph&acirc;n t&iacute;ch kỹ thuật tại c&aacute;c C&ocirc;ng ty chứng kho&aacute;n, c&ocirc;ng ty đầu tư, quỹ đầu tư.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><strong>Quyền lợi được hưởng</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Được hoạt động trong m&ocirc;i trường đầu tư năng động, chuy&ecirc;n nghiệp.</li>\r\n	<li>Thu nhập cạnh tranh.</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><strong>Y&ecirc;u cầu hồ sơ</strong></li>\r\n	<li>Hồ sơ dự tuyển bao gồm:</li>\r\n	<li>Đơn xin việc</li>\r\n	<li>Sơ yếu l&yacute; lịch (CV)<strong>&nbsp;</strong></li>\r\n</ol>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 6, Nh&agrave; D1, Ng&otilde; 181 Nguyễn Lương Bằng, Quận Đống Đa, H&agrave; Nội</p>\r\n\r\n<p><strong>Ứng tuyển ngay bằng c&aacute;ch gửi CV về email:&nbsp;dichvu@pif.vn</strong></p>\r\n\r\n<p><strong>Để biết th&ecirc;m chi tiết vui l&ograve;ng li&ecirc;n hệ:&nbsp;</strong>(024) 32 646 480</p>\r\n\r\n<p><strong>Hoặc truy cập website:</strong>&nbsp;<a href=\"https://old.pif.vn/\"><strong>https://old.pif.vn/</strong></a></p>', '0', '0', 0, '2019-04-18 09:09:21', 0, 0, '2019-04-18 09:09:21', '2019-04-18 09:09:21', '2019-04-18 09:09:21', 'recruitments/VMkvuKjIOsMbVZboQPZa2ofLshIAKQVO58YgliNK.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, nhân viên, Phân tích Kỹ thuật, Passion Investment', 'nhân viên', 0, 0, 'vietnam', 'hết hạn', NULL, '2019-04-18 02:09:21', '2023-02-24 00:43:48'),
(10, 'Tuyển dụng Chuyên viên tư vấn – Passion Investment', 'tuyen-dung-chuyen-vien-tu-van-passion-investment', '1. Mô tả công việc • Quản lý, chăm sóc và duy trì mối quan hệ lâu dài với tập khách hàng hiện tại của Công ty; • Tiếp nhận', '<p><strong>1. M&ocirc; tả c&ocirc;ng việc</strong></p>\r\n\r\n<p>&bull; Quản l&yacute;, chăm s&oacute;c v&agrave; duy tr&igrave; mối quan hệ l&acirc;u d&agrave;i với tập kh&aacute;ch h&agrave;ng hiện tại của C&ocirc;ng ty;<br />\r\n&bull; Tiếp nhận v&agrave; tư vấn dịch vụ đầu tư tới c&aacute;c nh&agrave; đầu tư tiềm năng li&ecirc;n hệ đến C&ocirc;ng ty qua hotline, website, email, Fanpage v&agrave; c&aacute;c k&ecirc;nh kh&aacute;c của C&ocirc;ng ty một c&aacute;ch nhanh ch&oacute;ng v&agrave; hiệu quả;<br />\r\n&bull; Chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; chủ động đề xuất c&aacute;c phương &aacute;n hỗ trợ kh&aacute;ch h&agrave;ng;</p>\r\n\r\n<p>&bull; Nắm bắt nhanh t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng v&agrave; tư vấn hợp l&yacute;;<br />\r\n&bull; Thực hiện c&aacute;c văn bản h&agrave;nh ch&iacute;nh, thủ tục giấy tờ li&ecirc;n quan đến kh&aacute;ch h&agrave;ng;</p>\r\n\r\n<p>&bull; Hỗ trợ v&agrave; triển khai c&aacute;c c&ocirc;ng việc kh&aacute;c của Bộ phận h&agrave;nh ch&iacute;nh theo y&ecirc;u cầu của Quản l&yacute;.</p>\r\n\r\n<p><br />\r\n<strong>2. Y&ecirc;u cầu c&ocirc;ng việc</strong><br />\r\n&bull; Ham học hỏi, năng động, hoạt b&aacute;t, chăm chỉ v&agrave; c&oacute; tr&aacute;ch nhiệm cao trong c&ocirc;ng việc;<br />\r\n&bull; Kỹ năng giao tiếp v&agrave; kỹ năng tư vấn kh&aacute;ch h&agrave;ng tốt;<br />\r\n&bull; Ưu ti&ecirc;n những bạn đ&atilde; c&oacute; kinh nghiệm về Telesales hoặc kinh nghiệm b&aacute;n h&agrave;ng trong bất kỳ loại h&igrave;nh dịch vụ n&agrave;o.</p>\r\n\r\n<p><br />\r\n<strong>3. Chế độ v&agrave; c&aacute;c quyền lợi</strong><br />\r\n&bull; Thu nhập 9-15 triệu, ổn định, kh&ocirc;ng &aacute;p doanh số;</p>\r\n\r\n<p>&bull; Thưởng cuối năm theo hiệu quả c&ocirc;ng việc, th&acirc;m ni&ecirc;n (1-3 th&aacute;ng lương hoặc hơn) v&agrave; c&aacute;c dịp lễ, Tết;</p>\r\n\r\n<p>&bull; Được đ&aacute;nh gi&aacute; hiệu quả c&ocirc;ng việc h&agrave;ng năm để điều chỉnh tăng lương;<br />\r\n&bull; Du lịch trong v&agrave; ngo&agrave;i nước;<br />\r\n&bull; Được l&agrave;m việc trong m&ocirc;i trường trẻ trung, năng động, s&aacute;ng tạo;</p>\r\n\r\n<p>&bull; Đươc đ&agrave;o tạo kiến thức về đầu tư/ t&agrave;i ch&iacute;nh v&agrave; ph&aacute;t triển nghề nghiệp một c&aacute;ch chuy&ecirc;n nghiệp;<br />\r\n&bull; L&agrave;m việc giờ h&agrave;nh ch&iacute;nh từ thứ 2 &ndash; thứ 6, s&aacute;ng từ 8h00 đến 11h30, chiều từ 13h đến 17h30 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail&nbsp;<a href=\"mailto:info@pif.vn\" target=\"_blank\">info@pif.vn</a>&nbsp;theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Chuy&ecirc;n vi&ecirc;n tư vấn hoặc gọi tới số (+84)24.73.067.868 để biết th&ecirc;m chi tiết.</strong></p>', '0', '0', 0, '2020-12-12 14:44:20', 0, 0, '2020-12-12 14:44:20', '2020-12-12 14:44:20', '2020-12-12 14:44:20', 'recruitments/qLyqk1v6ktRm3nY69qGJGuV8B899ZyPkaIRXM7MU.png', 'Tuyển dụng', 0, 'Tuyển dụng, Chuyên viên tư vấn, Passion Investment', 'Chuyên viên tư vấn', 0, 0, 'vietnam', 'hết hạn', NULL, '2020-12-12 07:44:20', '2023-02-24 00:43:23'),
(11, 'Tuyển dụng Nhân viên hành chính – Passion Investment', 'tuyen-dung-nhan-vien-hanh-chinh-passion-investment', '1. MÔ TẢ CÔNG VIỆC • Thực hiện các công việc liên quan tới Hành chính văn phòng • Thực hiện công tác văn thư, lưu trữ và quản lý', '<p><strong>1. M&Ocirc; TẢ C&Ocirc;NG VIỆC</strong><br />\r\n&bull; Thực hiện c&aacute;c c&ocirc;ng việc li&ecirc;n quan tới H&agrave;nh ch&iacute;nh văn ph&ograve;ng<br />\r\n&bull; Thực hiện c&ocirc;ng t&aacute;c văn thư, lưu trữ v&agrave; quản l&yacute; hồ sơ, giấy tờ, c&ocirc;ng văn đến, c&ocirc;ng văn đi trong c&ocirc;ng ty&hellip; một c&aacute;ch khoa học;<br />\r\n&bull; Chấm c&ocirc;ng, t&iacute;nh lương theo d&otilde;i thời gian l&agrave;m việc của nh&acirc;n sự;<br />\r\n&bull; Soạn thảo c&aacute;c văn bản h&agrave;nh ch&iacute;nh, hợp đồng, hồ sơ,&hellip;;<br />\r\n&bull; Trực điện thoại tiếp nhận th&ocirc;ng tin v&agrave; xử l&yacute; c&aacute;c vẫn đề li&ecirc;n quan với b&ecirc;n thứ ba;<br />\r\n&bull; Quản l&yacute; fanpage của c&ocirc;ng ty: tiếp nhận th&ocirc;ng tin kh&aacute;ch h&agrave;ng để lại để chuyển th&ocirc;ng tin cho Bộ phận tư vấn, hỗ trợ chăm s&oacute;c kh&aacute;ch h&agrave;ng;<br />\r\n&bull; Lập kế hoạch, mua sắm, cấp ph&aacute;t, gi&aacute;m s&aacute;t sử dụng VPP, m&aacute;y m&oacute;c, trang thiết bị văn ph&ograve;ng;<br />\r\n&bull; Hỗ trợ v&agrave; triển khai c&aacute;c c&ocirc;ng việc kh&aacute;c của Bộ phận tư vấn theo y&ecirc;u cầu của Quản l&yacute;.</p>\r\n\r\n<p><strong>2. Y&Ecirc;U CẦU ỨNG VI&Ecirc;N</strong><br />\r\n&bull; Ham học hỏi, năng động, hoạt b&aacute;t, chăm chỉ v&agrave; c&oacute; tr&aacute;ch nhiệm cao trong c&ocirc;ng việc;<br />\r\n&bull; Biết sắp xếp c&ocirc;ng việc v&agrave; quản l&yacute; thời gian tốt;<br />\r\n&bull; C&oacute; kỹ năng l&agrave;m việc với c&aacute;c b&ecirc;n thứ 3, c&aacute;c nh&agrave; cung cấp, l&agrave;m việc theo nh&oacute;m, l&agrave;m việc độc lập kỹ năng giao tiếp tốt;<br />\r\n&bull; Th&agrave;nh thạo MS Office: Word, excell,&hellip;<br />\r\n&bull; Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; kinh nghiệm &iacute;t nhất 2 năm về vị tr&iacute; h&agrave;nh ch&iacute;nh, kế to&aacute;n.</p>\r\n\r\n<p><strong>3. QUYỀN LỢI ĐƯỢC HƯỞNG</strong><br />\r\n&bull; Thu nhập 7-13 triệu, ổn định;<br />\r\n&bull; Thưởng cuối năm theo hiệu quả c&ocirc;ng việc, th&acirc;m ni&ecirc;n (1-3 th&aacute;ng lương hoặc hơn) v&agrave; c&aacute;c dịp lễ, Tết;<br />\r\n&bull; Được đ&aacute;nh gi&aacute; hiệu quả c&ocirc;ng việc h&agrave;ng năm để điều chỉnh tăng lương;<br />\r\n&bull; Du lịch trong v&agrave; ngo&agrave;i nước;<br />\r\n&bull; Được l&agrave;m việc trong m&ocirc;i trường trẻ trung, năng động, s&aacute;ng tạo;<br />\r\n&bull; Đươc đ&agrave;o tạo kiến thức về đầu tư/ t&agrave;i ch&iacute;nh v&agrave; ph&aacute;t triển nghề nghiệp một c&aacute;ch chuy&ecirc;n nghiệp;<br />\r\n&bull; L&agrave;m việc giờ h&agrave;nh ch&iacute;nh từ thứ 2 &ndash; thứ 6, s&aacute;ng từ 8h00 đến 11h30, chiều từ 13h đến 17h30 (nghỉ thứ 7, chủ nhật). Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN.</p>\r\n\r\n<p><strong>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail&nbsp;<a href=\"mailto:info@pif.vn\" target=\"_blank\">info@pif.vn</a>&nbsp;theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Nh&acirc;n vi&ecirc;n h&agrave;nh ch&iacute;nh hoặc gọi tới số (+84)24.73.067.868 để biết th&ecirc;m chi tiết.</strong></p>', '0', '0', 0, '2021-05-31 09:34:20', 0, 0, '2021-05-31 09:34:20', '2021-05-31 09:34:20', '2021-05-31 09:34:20', 'recruitments/dKPnRbBUjRZYKrTPbrs8UtXUBAqBqilku5jdSQE3.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Nhân viên hành chính, Passion Investment', 'Nhân viên hành chính', 0, 0, 'vietnam', 'hết hạn', NULL, '2021-05-31 02:34:20', '2023-02-24 00:42:59'),
(12, 'Tuyển dụng Kế toán tổng hợp – Passion Investment', 'tuyen-dung-ke-toan-tong-hop-passion-investment', '1. Mô tả công việc Thực hiện các nhiệm vụ bảo hiểm, tiền lương. Theo dõi và quản lý các tài khoản ngân hàng, thực hiện giao dịch với ngân', '<p><img alt=\"\" src=\"/ckfinder/userfiles/images/tuyen-dung-ke-toan-tong-hop-passion-investment.jpg\" style=\"height:573px; width:800px\" /></p>\r\n\r\n<p><strong>1. M&ocirc; tả c&ocirc;ng việc</strong></p>\r\n\r\n<ul>\r\n	<li>Thực hiện c&aacute;c nhiệm vụ bảo hiểm, tiền lương.</li>\r\n	<li>Theo d&otilde;i v&agrave; quản l&yacute; c&aacute;c t&agrave;i khoản ng&acirc;n h&agrave;ng, thực hiện giao dịch với ng&acirc;n h&agrave;ng.</li>\r\n	<li>Theo d&otilde;i, kiểm tra v&agrave; kiểm so&aacute;t tất cả c&aacute;c khoản thanh to&aacute;n trước khi thực hiện.</li>\r\n	<li>Trực tiếp xử l&yacute; v&agrave; hạch to&aacute;n c&aacute;c nghiệp vụ kế to&aacute;n.</li>\r\n	<li>Kiểm tra, đối chiếu số liệu, sổ s&aacute;ch thu &ndash; chi.</li>\r\n	<li>Kiểm tra, định khoản nghiệp vụ ph&aacute;t sinh.</li>\r\n	<li>Hạch to&aacute;n, quyết to&aacute;n thu nhập, chi ph&iacute;, khấu hao, TSCĐ, c&ocirc;ng nợ, thuế GTGT v&agrave; b&aacute;o c&aacute;o thuế.</li>\r\n	<li>Theo d&otilde;i kiểm tra tất cả c&aacute;c h&oacute;a đơn khai thuế h&agrave;ng th&aacute;ng</li>\r\n	<li>Hạch to&aacute;n nghiệp vụ ph&aacute;t sinh, chứng từ, sổ s&aacute;ch kế to&aacute;n tr&ecirc;n phần mềm</li>\r\n	<li>Đảm bảo tất cả c&aacute;c vấn đề thuế được xử l&yacute; đ&uacute;ng v&agrave; ph&ugrave; hợp với quy định hiện h&agrave;nh.</li>\r\n	<li>Lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh theo từng qu&yacute;, 6 th&aacute;ng, năm v&agrave; c&aacute;c b&aacute;o c&aacute;o giải tr&igrave;nh chi tiết. Nộp B&aacute;o c&aacute;o thuế v&agrave; TNCN theo qu&yacute; theo qui định/y&ecirc;u cầu của cơ quan quản l&yacute; nh&agrave; nước,</li>\r\n	<li>Thực hiện nhiệm vụ kh&aacute;c theo sự ph&acirc;n c&ocirc;ng của BLĐ</li>\r\n</ul>\r\n\r\n<p><strong>2. Y&ecirc;u cầu ứng vi&ecirc;n</strong></p>\r\n\r\n<ul>\r\n	<li>Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh Kế to&aacute;n, T&agrave;i ch&iacute;nh, Kiểm to&aacute;n</li>\r\n	<li>C&oacute; &iacute;t nhất 03 năm kinh nghiệm vị tr&iacute; kế to&aacute;n tổng hợp (bắt buộc)</li>\r\n	<li>C&oacute; tư duy v&agrave; ph&acirc;n t&iacute;ch, tổng hợp vấn đề tốt</li>\r\n	<li>Nắm vững quy định Ph&aacute;p luật trong lĩnh vực Kế to&aacute;n</li>\r\n	<li>Nghiệp vụ tổng hợp, ph&acirc;n t&iacute;ch v&agrave; lập b&aacute;o c&aacute;o tốt.</li>\r\n	<li>Sử dụng th&agrave;nh thạo tin học văn ph&ograve;ng, kỹ năng excel, phần mềm kế to&aacute;n</li>\r\n	<li>T&iacute;nh c&aacute;ch: Trung thực, Cẩn thận v&agrave; chăm chỉ, giao tiếp tốt.</li>\r\n	<li>Chịu được &aacute;p lực cao trong c&ocirc;ng việc.</li>\r\n	<li>Tuổi từ 25 đến 40</li>\r\n</ul>\r\n\r\n<p><strong>3. Quyền lợi của ứng vi&ecirc;n</strong></p>\r\n\r\n<ul>\r\n	<li>Thu nhập khởi điểm 10-12 triệu;</li>\r\n	<li>L&agrave;m việc giờ h&agrave;nh ch&iacute;nh từ thứ 2 &ndash; thứ 6;</li>\r\n	<li>Thưởng cuối năm theo hiệu quả c&ocirc;ng việc, th&acirc;m ni&ecirc;n (1-3 th&aacute;ng lương hoặc hơn);</li>\r\n	<li>Nghỉ lễ tết hưởng nguy&ecirc;n lương;</li>\r\n	<li>Được đ&aacute;nh gi&aacute; hiệu quả c&ocirc;ng việc h&agrave;ng năm để điều chỉnh tăng lương;</li>\r\n	<li>Du lịch trong v&agrave; ngo&agrave;i nước;</li>\r\n	<li>Được l&agrave;m việc trong m&ocirc;i trường trẻ trung, năng động, s&aacute;ng tạo;</li>\r\n	<li>Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN.</li>\r\n</ul>\r\n\r\n<p><strong>4. Y&ecirc;u cầu hồ sơ</strong></p>\r\n\r\n<p>Hồ sơ dự tuyển bao gồm:</p>\r\n\r\n<p>Sơ yếu l&yacute; lịch (CV)</p>\r\n\r\n<p>Bằng cấp, bảng điểm k&egrave;m theo.</p>\r\n\r\n<p><strong>5. Địa điểm l&agrave;m việc</strong>: Tầng 3, t&ograve;a nh&agrave; FS Gold Season, 47 Nguyễn Tu&acirc;n, Thanh Xu&acirc;n Trung, Thanh Xu&acirc;n, H&agrave; Nội.</p>\r\n\r\n<p><strong>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail: thongtin@pif.vn theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Kế to&aacute;n tổng hợp.</strong></p>\r\n\r\n<p><strong>Ngo&agrave;i ra, ứng vi&ecirc;n c&oacute; thể gọi tới số (+84) 24.73.067.868 hoặc truy cập website:&nbsp;<a href=\"https://old.pif.vn/\">https://pif.vn</a>&nbsp;để biết th&ecirc;m chi tiết.</strong></p>', '0', '0', 0, '2022-02-21 16:50:21', 0, 0, '2022-02-21 16:50:21', '2022-02-21 16:50:21', '2022-02-21 16:50:21', 'recruitments/bjxImPWPJbwKIqRTRnzN2BEsU2NH5E9sbGhbevwy.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Kế toán tổng hợp, Passion Investment', 'Kế toán tổng hợp', 0, 0, 'vietnam', 'hết hạn', NULL, '2022-02-21 09:50:21', '2023-02-24 00:42:41');
INSERT INTO `pif_recruitment` (`id`, `title`, `slug`, `introtext`, `fulltext`, `state`, `catid`, `created_by`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `attribs`, `ordering`, `metakey`, `metadesc`, `hits`, `featured`, `language`, `note`, `remember_token`, `created_at`, `updated_at`) VALUES
(13, 'Tuyển dụng Nhân viên lái xe hành chính – Passion Investment', 'tuyen-dung-nhan-vien-lai-xe-hanh-chinh-passion-investment', 'Mô tả công việc – Lái xe đưa đón lãnh đạo công ty. – Đưa đón, chuyên chở CBCNV Công ty hoặc khách trong quá trình thực hiện công việc và liên hệ công tác.', '<p><img alt=\"\" src=\"/ckfinder/userfiles/images/tuyen-dung-lai-xe.jpg\" style=\"height:960px; width:960px\" /></p>\r\n\r\n<p><strong>M&ocirc; tả c&ocirc;ng việc</strong></p>\r\n\r\n<p>&ndash; L&aacute;i xe đưa đ&oacute;n l&atilde;nh đạo c&ocirc;ng ty.</p>\r\n\r\n<p>&ndash; Đưa&nbsp;đ&oacute;n, chuy&ecirc;n chở&nbsp;CBCNV C&ocirc;ng ty hoặc kh&aacute;ch trong qu&aacute;&nbsp;tr&igrave;nh thực hiện c&ocirc;ng việc v&agrave;&nbsp;li&ecirc;n hệ&nbsp;c&ocirc;ng t&aacute;c.</p>\r\n\r\n<p>&ndash; Bảo quản v&agrave;&nbsp;giữ&nbsp;g&igrave;n xe tốt.&nbsp;Đảm bảo xe lu&ocirc;n sạch sẽ&nbsp;v&agrave;&nbsp;trong t&igrave;nh trạng sẵn s&agrave;ng phục vụ. Lu&ocirc;n kiểm tra xe nhằm ph&aacute;t hiện v&agrave;&nbsp;khắc phục kịp thời c&aacute;c hư&nbsp;hỏng, bảo tr&igrave;&nbsp;xe theo&nbsp;định kỳ.</p>\r\n\r\n<p>&ndash; Thực hiện c&aacute;c c&ocirc;ng việc kh&aacute;c theo quy định của cấp tr&ecirc;n.</p>\r\n\r\n<p>&ndash; Thời gian l&agrave;m theo t&iacute;nh chất của c&ocirc;ng việc v&agrave; sự sắp xếp điều động của C&ocirc;ng ty.</p>\r\n\r\n<p><strong>Y&ecirc;u cầu ứng vi&ecirc;n</strong></p>\r\n\r\n<p>&ndash; Nam giới tuổi từ 25 &ndash; 40, c&oacute; bằng B trở l&ecirc;n.</p>\r\n\r\n<p>&ndash; Kh&ocirc;ng h&uacute;t thuốc.</p>\r\n\r\n<p>&ndash; Kỹ năng l&aacute;i xe tốt, t&aacute;c phong chuẩn mực, giao tiếp h&ograve;a đồng, th&acirc;n thiện.</p>\r\n\r\n<p>&ndash; Sức khỏe tốt, nhanh nhẹn, trung thực, nhiệt t&igrave;nh.</p>\r\n\r\n<p>&ndash; C&oacute; kinh nghiệm từ 2 năm trở l&ecirc;n l&aacute;i xe cho Văn ph&ograve;ng, C&ocirc;ng ty.</p>\r\n\r\n<p>&ndash; Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; kinh nghiệm l&aacute;i xe cho L&atilde;nh đạo.</p>\r\n\r\n<p>&ndash; Ưu ti&ecirc;n ứng vi&ecirc;n ở tại khu vực Thanh Xu&acirc;n, Đống Đa, H&agrave; Đ&ocirc;ng.</p>\r\n\r\n<p><strong>Quyền lợi của ứng vi&ecirc;n</strong></p>\r\n\r\n<p>&ndash; Thu nhập 8-10 triệu, ổn định;</p>\r\n\r\n<p>&ndash; Thưởng cuối năm theo hiệu quả c&ocirc;ng việc v&agrave; c&aacute;c dịp lễ, Tết;</p>\r\n\r\n<p>&ndash; Du lịch trong v&agrave; ngo&agrave;i nước;</p>\r\n\r\n<p>&ndash; Được l&agrave;m việc trong m&ocirc;i trường trẻ trung, năng động, s&aacute;ng tạo;</p>\r\n\r\n<p>&ndash; Hưởng đầy đủ c&aacute;c chế đ&ocirc; BHXH, BHYT, BHTN.</p>\r\n\r\n<p><strong>Y&ecirc;u cầu hồ sơ</strong></p>\r\n\r\n<p>Hồ sơ dự tuyển bao gồm:</p>\r\n\r\n<p>Sơ yếu l&yacute; lịch (CV)</p>\r\n\r\n<p>Bằng B2</p>\r\n\r\n<p><strong>Địa điểm l&agrave;m việc</strong>: Tầng 3, t&ograve;a nh&agrave; FS Gold Season, 47 Nguyễn Tu&acirc;n, Thanh Xu&acirc;n Trung, Thanh Xu&acirc;n, H&agrave; Nội.</p>\r\n\r\n<p>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail: thongtin@pif.vn theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Nh&acirc;n vi&ecirc;n l&aacute;i xe h&agrave;nh ch&iacute;nh.</p>\r\n\r\n<p>Ngo&agrave;i ra, ứng vi&ecirc;n c&oacute; thể gọi tới số (+84) 24.73.067.868 hoặc truy cập website:<a href=\"https://pif.vn/\" target=\"_blank\"><strong>&nbsp;https://pif.vn</strong></a>&nbsp;để biết th&ecirc;m chi tiết.</p>', '0', '0', 0, '2022-03-30 12:50:22', 0, 0, '2022-03-30 12:50:22', '2022-03-30 12:50:22', '2022-03-30 12:50:22', 'recruitments/OmzuhrpcOom4pnCB9MoFDlmuBF2OlYVECSOVK1rN.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Nhân viên lái xe hành chính, Passion Investment', 'Nhân viên lái xe hành chính', 0, 0, '', 'hết hạn', NULL, '2022-03-30 05:50:22', '2023-02-24 00:42:26'),
(14, 'Tuyển dụng Chuyên viên Phân tích kỹ thuật – Passion Investment', 'tuyen-dung-chuyen-vien-phan-tich-ky-thuat-passion-investmentMô tả công việc Phụ trách phân tích kỹ thuật về thị trường chứng khoán và các cổ phiếu được yêu cầu. Đề xuất ý tưởng đầu tư dựa trên', 'Mô tả công việc Phụ trách phân tích kỹ thuật về thị trường chứng khoán và các cổ phiếu được yêu cầu. Đề xuất ý tưởng đầu tư dựa trên', '<p><img alt=\"\" src=\"/ckfinder/userfiles/images/tuyen-dung-chuyen-vien-phan-tich-ky-thuat-passion-investment.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<ol>\r\n	<li><strong>M&ocirc; tả c&ocirc;ng việc</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Phụ tr&aacute;ch ph&acirc;n t&iacute;ch kỹ thuật về thị trường chứng kho&aacute;n v&agrave; c&aacute;c cổ phiếu được y&ecirc;u cầu.</li>\r\n	<li>Đề xuất &yacute; tưởng đầu tư dựa tr&ecirc;n ph&acirc;n t&iacute;ch kỹ thuật, theo d&otilde;i c&aacute;c khoản đầu tư được duyệt.</li>\r\n	<li>Tham gia v&agrave;o qu&aacute; tr&igrave;nh đầu tư v&agrave; quản l&yacute; danh mục.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><strong>Y&ecirc;u cầu c&ocirc;ng việc</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, kinh tế, đầu tư, chứng kho&aacute;n.</li>\r\n	<li>C&oacute; kiến thức về ph&acirc;n t&iacute;ch kỹ thuật v&agrave; đ&atilde; c&oacute; kinh nghiệm ứng dụng thực tiễn.</li>\r\n	<li>Sử dụng th&agrave;nh thạo c&aacute;c phần mềm ph&acirc;n t&iacute;ch kỹ thuật.</li>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; chứng chỉ về ph&acirc;n t&iacute;ch kỹ thuật (CMT) hoặc đ&atilde; từng c&oacute; c&oacute; kinh nghiệm l&agrave;m Ph&acirc;n t&iacute;ch kỹ thuật tại c&aacute;c C&ocirc;ng ty chứng kho&aacute;n, c&ocirc;ng ty đầu tư, quỹ đầu tư.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><strong>Quyền lợi được hưởng</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Được hoạt động trong m&ocirc;i trường đầu tư năng động, chuy&ecirc;n nghiệp, tham gia v&agrave;o c&aacute;c quyết định đầu tư của c&ocirc;ng ty.</li>\r\n	<li>Thu nhập cạnh tranh theo năng lực.</li>\r\n	<li>Được đ&aacute;nh gi&aacute; hiệu quả c&ocirc;ng việc h&agrave;ng năm để điều chỉnh tăng lương.</li>\r\n	<li>Du lịch trong v&agrave; ngo&agrave;i nước.</li>\r\n	<li>Được l&agrave;m việc trong m&ocirc;i trường trẻ trung, năng động, s&aacute;ng tạo.</li>\r\n	<li>Hưởng đầy đủ c&aacute;c chế độ BHXH, BHYT, BHTN.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><strong>Y&ecirc;u cầu hồ sơ</strong></li>\r\n</ol>\r\n\r\n<p>Hồ sơ dự tuyển bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Sơ yếu l&yacute; lịch (CV)</li>\r\n	<li>Bằng cấp, bảng điểm k&egrave;m theo.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><strong>Địa điểm l&agrave;m việc:</strong>&nbsp;Tầng 3, t&ograve;a nh&agrave; FS Gold Season, 47 Nguyễn Tu&acirc;n, Thanh Xu&acirc;n Trung, Thanh Xu&acirc;n, H&agrave; Nội.</li>\r\n</ol>\r\n\r\n<p>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail: thongtin@pif.vn theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Chuy&ecirc;n vi&ecirc;n Ph&acirc;n t&iacute;ch kỹ thuật.</p>\r\n\r\n<p>Ngo&agrave;i ra, ứng vi&ecirc;n c&oacute; thể gọi tới số (+84) 24.73.067.868 hoặc truy cập website:&nbsp;<a href=\"https://pif.vn/\"><strong>https://pif.vn</strong></a>&nbsp;để biết th&ecirc;m chi tiết.</p>', '0', '0', 0, '2022-04-04 10:58:53', 0, 0, '2022-04-04 10:58:53', '2022-04-04 10:58:53', '2022-04-04 10:58:53', 'recruitments/mwyyk3ZxmTQN9XAljEionMVxj8rojdJCniEcbkQ6.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Chuyên viên Phân tích kỹ thuật, Passion Investment', 'Chuyên viên Phân tích kỹ thuật', 0, 0, 'vietnam', '', NULL, '2022-04-04 03:58:53', '2023-02-24 00:42:07'),
(15, 'Tuyển dụng vị trí Chuyên viên Đầu tư – Passion Investment', 'tuyen-dung-vi-tri-chuyen-vien-dau-tu-passion-investment', 'Tuyển dụng Chuyên viên Đầu tư– Passion Investment Mô tả công việc Tìm kiếm, nghiên cứu và đề xuất các cơ hội đầu tư vào cổ phiếu cho hoạt động', '<p><img alt=\"\" src=\"/ckfinder/userfiles/images/pif-tuyen-dung-1536x864.jpg\" style=\"height:864px; width:1536px\" /></p>\r\n\r\n<p><strong>Tuyển dụng Chuy&ecirc;n vi&ecirc;n Đầu tư&ndash; Passion Investment</strong></p>\r\n\r\n<ol>\r\n	<li>M&ocirc; tả c&ocirc;ng việc</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>T&igrave;m kiếm, nghi&ecirc;n cứu v&agrave; đề xuất c&aacute;c cơ hội đầu tư v&agrave;o cổ phiếu cho hoạt động đầu tư của c&ocirc;ng ty.</li>\r\n	<li>Thực hiện c&aacute;c b&aacute;o c&aacute;o ph&acirc;n t&iacute;ch, x&acirc;y dựng model định gi&aacute; cổ phiếu v&agrave; đề xuất chi tiết nhằm đ&aacute;p ứng nhu cầu đầu v&agrave;o cho c&aacute;c danh mục đầu tư của c&ocirc;ng ty, hỗ trợ qu&aacute; tr&igrave;nh ra quyết định của Gi&aacute;m đốc Đầu tư.</li>\r\n	<li>X&acirc;y dựng v&agrave; duy tr&igrave; mối quan hệ với c&aacute;c doanh nghiệp, c&aacute;c Ng&acirc;n h&agrave;ng, c&aacute;c định chế t&agrave;i ch&iacute;nh c&oacute; li&ecirc;n quan đến hoạt động đầu tư của c&ocirc;ng ty.</li>\r\n	<li>Khảo s&aacute;t, kết nối, t&igrave;m kiếm th&ocirc;ng tin về c&aacute;c doanh nghiệp, c&aacute;c ng&agrave;nh được giao nghi&ecirc;n cứu</li>\r\n	<li>X&acirc;y dựng cơ sở dữ liệu phục vụ nghi&ecirc;n cứu về vĩ m&ocirc;, thị trường, chỉ số, ng&agrave;nh</li>\r\n	<li>X&acirc;y dựng hệ thống bộ lọc cổ phiếu, ng&agrave;nh ph&ugrave; hợp cho từng giai đoạn của thị trường</li>\r\n	<li>Tham gia v&agrave;o qu&aacute; tr&igrave;nh đầu tư, quản trị danh mục/cổ phiếu</li>\r\n	<li>Thực hiện c&aacute;c y&ecirc;u cầu kh&aacute;c của cấp tr&ecirc;n.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>Y&ecirc;u cầu ứng vi&ecirc;n</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, kinh tế, đầu tư, chứng kho&aacute;n.</li>\r\n	<li>C&oacute; niềm đam m&ecirc; với c&ocirc;ng việc đầu tư, đ&atilde; c&oacute; kinh nghiệm đầu tư chứng kho&aacute;n thực tế.</li>\r\n	<li>C&oacute; kiến thức cơ bản về t&agrave;i ch&iacute;nh, kinh doanh, đầu tư.</li>\r\n	<li>Khả năng ph&acirc;n t&iacute;ch, tổng hợp dữ liệu</li>\r\n	<li>Tư duy logic, c&oacute; tinh thần cầu tiến v&agrave; chịu được &aacute;p lực c&ocirc;ng việc cao.</li>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute; chứng chỉ CFA level I trở l&ecirc;n, sử dụng tốt tiếng Anh.</li>\r\n	<li>Ưu ti&ecirc;n ứng vi&ecirc;n đ&atilde; c&oacute; tr&ecirc;n 2 năm kinh nghiệm l&agrave;m việc tại c&aacute;c c&ocirc;ng ty chứng kho&aacute;n, c&ocirc;ng ty đầu tư.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>Quyền lợi được hưởng</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Được hoạt động trong m&ocirc;i trường đầu tư năng động, chuy&ecirc;n nghiệp, tham gia v&agrave;o c&aacute;c quyết định đầu tư của c&ocirc;ng ty. (L&agrave;m việc trong m&ocirc;i trường với đội ngũ t&agrave;i năng, gi&agrave;u kinh nghiệm tr&ecirc;n thị trường)</li>\r\n	<li>Thu nhập cạnh tranh theo năng lực.</li>\r\n	<li>Được đ&aacute;nh gi&aacute; hiệu quả c&ocirc;ng việc h&agrave;ng năm để điều chỉnh tăng lương. (review hiệu quả c&ocirc;ng việc v&agrave; x&eacute;t tăng lương h&agrave;ng năm)</li>\r\n	<li>Du lịch trong v&agrave; ngo&agrave;i nước.</li>\r\n	<li>Hưởng đầy đủ c&aacute;c chế độ BHXH, BHYT, BHTN.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>Y&ecirc;u cầu hồ sơ</li>\r\n</ol>\r\n\r\n<p>Hồ sơ dự tuyển bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Sơ yếu l&yacute; lịch (CV)</li>\r\n	<li>Bằng cấp, bảng điểm k&egrave;m theo.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>Địa điểm l&agrave;m việc: Tầng 3, t&ograve;a nh&agrave; FS Gold Season, 47 Nguyễn Tu&acirc;n, Thanh Xu&acirc;n Trung, Thanh Xu&acirc;n, H&agrave; Nội.</li>\r\n</ol>\r\n\r\n<p><strong>Ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ đến địa chỉ mail: thongtin@pif.vn theo c&uacute; ph&aacute;p [họ t&ecirc;n] + Ứng tuyển Chuy&ecirc;n vi&ecirc;n Đầu tư.</strong></p>\r\n\r\n<p><strong>https://old.pif.vn/tuyen-dung-vi-tri-chuyen-vien-dau-tu&hellip;/</strong></p>\r\n\r\n<p><strong>Ngo&agrave;i ra, ứng vi&ecirc;n c&oacute; thể gọi tới số (+84) 24.73.067.868 hoặc truy cập website: https://old.pif.vn/ để biết th&ecirc;m chi tiết.</strong></p>', '0', '0', 0, '2022-06-09 10:58:54', 0, 0, '2022-06-09 10:58:54', '2022-06-09 10:58:54', '2022-06-09 10:58:54', 'recruitments/mEJWqdQh6E9piEgdwLcLYCiHxYslFCWPBz2OlS2m.jpg', 'Tuyển dụng', 0, 'Tuyển dụng, Chuyên viên Đầu tư, Passion Investment', 'Chuyên viên Đầu tư', 0, 0, 'vietnam', '', NULL, '2022-06-09 03:58:54', '2023-02-24 00:46:08');

