  `user_type` int(11) NOT NULL DEFAULT '0',
  `created_at` timestamp NOT NULL,
  `last_login` timestamp NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

